.upload-input::-webkit-file-upload-button {
    visibility: hidden;
}
.upload-input::before {
    content: 'UPLOAD FILE';
    color: #bfbfbf;
    display: inline-block;
    background: -webkit-linear-gradient(top, #151515, #151515);
    border: 1px solid #1e1e1e;
    border-radius: 3px;
    padding: 5px 50px;
    outline: none;
    white-space: nowrap;
    -webkit-user-select: none;
    cursor: pointer;
    font-weight: 700;
    font-size: 10pt;
}
.upload-input:hover::before {
    border-color: #ffffff1c;
}
.upload-input:hover::before {
    transition: border-color .3s;
}
.upload-input:active {
    outline: 0;
}
.upload-input:active::before {
    background: -webkit-linear-gradient(top, #121212, #121212);
}


