    --outline-color: /*[[outlinecolor]]*/;
    --outline-color-hover: /*[[outlinecolorhover]]*/;
    
    --transparent-color: /*[[transparentcolor]]*/;
    --solid-color: /*[[solidcolor]]*/;
    
