._2S8RD:hover ._3ApeV {
    outline: 3px var(--outline-color-hover) solid;
    box-shadow: 0 0 15px var(--outline-color-hover);
}
  
._2YkEf,
._2S8RD {
      /*[[willchange]]*/
}
  
._2YkEf:hover,
._2S8RD:hover {
    transform: var(--hover-transform);
    z-index: 2;
}
