.Box--overlay {
	border: none !important;
	border-color: unset !important;
	border-radius: 4px;
	box-shadow: 0 0 18px rgba(0, 0, 0, .4) !important;
}

/* PR Button fix - No ripple, unfortunately */
.js-mrege-method-menu-button::after,
.select-menu-button::after {
	transform: unset !important;
	opacity: unset !important;
	top: unset;
	left: unset;
	width: unset;
	height: unset;
	display: inline-block;
	position: relative;
}

