}

/* Extra Inputs */
.form_control {
	border-color: revert !important;
}

.form_group > label {
	top: 31px;
	left: 15px;
	position: relative;
	padding: 0px 5px 0px 5px;
	font-size: 1.1em;
	transition: 0.2s;
	pointer-events: none;
}

.form_group.label-animate > label {
	background-color: white;
	top: 12px !important;
	left: 16px !important;
	font-size: 0.9em;
}

.form_control:focus {
	background-color: unset !important;
	border: revert;
	border-color: #2c3e50 !important;
}

/* Fix header search bar */
input.input-sm.header-search-input {
	background: none !important;
	height: initial !important;
	line-height: unset !important;
}

input.input-sm.header-search-input:focus {
