/*--按钮*/
.am-btn{
    outline:none !important;
    border:none !important;
    letter-spacing: .05em;
    padding: 8px 18px;
    transition: all .15s ease;
    box-shadow: 0 4px 6px rgba(50,50,93,.11), 0 1px 3px rgba(0,0,0,.08);
    color: #fff !important;
    margin-bottom:6px;
}
.am-dropdown > .am-btn{
    color: inherit !important;
}
button[type='button']{
    outline:none !important;
    border:none !important;
    box-shadow: 0 4px 6px rgba(50,50,93,.11), 0 1px 3px rgba(0,0,0,.08);
    transition: all .15s ease;
    margin-bottom:6px;
    padding: 8px 18px;
}
