/*--页码*/
.am-pagination , .page-area{
    padding-top:20px;
    padding-bottom:20px;
    width:max-content;
    width:-moz-max-content;
    margin-left:auto;
    margin-right:auto;
    border-top: none !important;
}
.am-pagination > li > a , .page-area .page-bar > button{
    outline:none !important;
    cursor: pointer;
    line-height: 1.25;
    position: relative;
    display: block;
    margin-left: -.0625rem;
    padding: .5rem .75rem;
    color: #8898aa;
    border: .0625rem solid #dee2e6;
    background-color: #fff;
    font-size: 15px;
    font-weight: unset;
    display: flex;
    min-width: 36px;
    height: 36px;
    margin: 0 3px;
    border-radius: 100px!important;
