    box-shadow: 0 4px 6px rgba(50,50,93,.11), 0 1px 3px rgba(0,0,0,.08);
    color: #fafafa !important;
    height:unset;
    line-height:unset;
    border-radius:5px !important;
    margin-right:3px;
    display:inline-block !important;
}
