@-moz-document url-prefix("https://www.notion.so/") {
/*
* Author: Eduardo Lacerda (Brazil)
* Based on: 'Notion.so - Scrollbar' by Steve Yang
*/


/** Cursor On Scrollbars **/
.notion-selectable {
    cursor: text;
}

.notion-scroller.horizontal {
    cursor: auto;
}

.notion-scroller.vertical {
	z-index: 2;
}


/** Left vertical scrollbar **/
.notion-sidebar .notion-scroller.vertical::-webkit-scrollbar {
	width: 10px;
}

.notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-thumb {
	border-radius: 4px;
}

.notion-dark-theme .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-track {
	background: rgb(66, 73, 78)
}

.notion-dark-theme .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-thumb {
    background: rgb(117, 117, 117);
}

.notion-dark-theme .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-thumb:hover {
    background: rgb(152, 152, 152);
}

.notion-light-theme .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-track {
    background: rgb(232, 232, 232);
}

.notion-light-theme .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-thumb {
    background: rgb(179, 179, 179);
}

.notion-light-theme .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-thumb:hover {
    background: rgb(144, 144, 144);
}



/** Right vertical scrollbar **/
.notion-frame .notion-scroller.vertical::-webkit-scrollbar {
	width: 12px;
}

.notion-frame .notion-scroller.vertical::-webkit-scrollbar-thumb {
    border-radius: 4px;
}

.notion-dark-theme .notion-frame .notion-scroller.vertical::-webkit-scrollbar-track {
	background: rgb(66, 73, 78)
}
.notion-dark-theme .notion-frame .notion-scroller.vertical::-webkit-scrollbar-thumb {
	background: rgb(117, 117, 117);
}

.notion-dark-theme .notion-frame .notion-scroller.vertical::-webkit-scrollbar-thumb:hover {
    background: rgb(152, 152, 152);
}

.notion-light-theme .notion-frame .notion-scroller.vertical::-webkit-scrollbar-track {
    background: rgb(232, 232, 232);
}
.notion-light-theme .notion-frame .notion-scroller.vertical::-webkit-scrollbar-thumb {
    background: rgb(179, 179, 179);
}

.notion-light-theme .notion-frame .notion-scroller.vertical::-webkit-scrollbar-thumb:hover {
    background: rgb(144, 144, 144);
}


/** Horizontal scrollbar **/
.notion-selectable .notion-scroller.horizontal::-webkit-scrollbar {
    height: 12px;
}

.notion-selectable .notion-scroller.horizontal::-webkit-scrollbar-track{
    margin-left: 110px;
    margin-right: 29px;
    border-radius: 20px;
}

