.custom-field-front-badges > span > div:nth-child(1) { order: 1; }    
.custom-field-front-badges > span > div:nth-child(2) { order: 2; } 

.custom-field-front-badges > span > div:nth-child(3) { order: 3; }   
.custom-field-front-badges > span > div:nth-child(4) { order: 4; }   

.custom-field-front-badges > span > div:nth-child(5) { order: 5; 
display: grid;
     grid-template-columns: 2fr 1fr 2fr ;
  } 

.custom-field-front-badges > span > div:nth-child(5):after { 
 
    content: "➔";
  font-weight: 200;
 font-size: 10px;
    color: rgba(0,0,0,0.4);
   text-align: center;

 
    

}    
 

