@-moz-document domain("removeddit.com") {
/*

Copyright 2020 Xeoth

Permission to use, copy, modify, and/or distribute this software for any purpose with or without fee is hereby granted, provided that the above copyright notice and this permission notice appear in all copies.

THE SOFTWARE IS PROVIDED "AS IS" AND THE AUTHOR DISCLAIMS ALL WARRANTIES WITH REGARD TO THIS SOFTWARE INCLUDING ALL IMPLIED WARRANTIES OF MERCHANTABILITY AND FITNESS. IN NO EVENT SHALL THE AUTHOR BE LIABLE FOR ANY SPECIAL, DIRECT, INDIRECT, OR CONSEQUENTIAL DAMAGES OR ANY DAMAGES WHATSOEVER RESULTING FROM LOSS OF USE, DATA OR PROFITS, WHETHER IN AN ACTION OF CONTRACT, NEGLIGENCE OR OTHER TORTIOUS ACTION, ARISING OUT OF OR IN CONNECTION WITH THE USE OR PERFORMANCE OF THIS SOFTWARE.

*/

html, body, #app {
  background-color: white;
}

/* thread list */
.thread.removed {
  background-color: white;
  border: 2px solid red;
  border-radius: 3px;
}

.thread.removed .thread-title {
  color: red;
}

.thread.removed .thread-title::after {
  content: " - ❌ Removed by subreddit moderators";
  font-size: 0.75em;
}

/* voting -> score */
.thread .vote {
  visibility: hidden;
}

.thread .thread-score {
  margin-top: -10px;
}

.thread .thread-score::before {
  content: "Score: "
}

/* threads/comments */

#comment-info .removed-text {
  color: red;
}

#comment-info .deleted-text {
  color: orange;
}

.comment {
  background-color: white;
  border: 1px solid black;
  color: black;
}

.comment.comment-even {
  background-color: #f9f9f9;
}

/* deleted comments */
.comment.deleted {
  color: orange;
  border: 1px solid orange;
}

.comment.deleted > .comment-head .comment-time::after {
  content: " - ⚠ Deleted by user";
  font-weight: bold;
  color: darkorange;
}


/* removed comments */
.comment.removed {
  color: red;
  border: 1px solid red;
}

.comment.removed > .comment-head .comment-time::after {
  content: " - ❌ Removed by subreddit moderators";
  font-weight: bold;
  color: crimson;
}


/* credits */
div#app::after {
  content: "Stylesheet by /u/Xeoth";
  color: grey;
  margin-left: 5px;
}

}
