@-moz-document domain("twitch.tv")
{

.top-nav:hover {
  z-index: 10000 !important;
}

.top-bar {
  justify-content: flex-start !important;
}

.persistent-player {
  width: 100% !important;
}

.player-controls__right-control-group {
  justify-content: flex-start !important;
}

.settings-menu-button-component .tw-balloon--down.tw-balloon--right,
.settings-menu-button-component .tw-balloon--up.tw-balloon--right {
  left: 0 !important;
  right: auto !important;
}

.prime-offers .tw-balloon--down.tw-balloon--right,
.prime-offers .tw-balloon--up.tw-balloon--right {
  right: 100px !important;
}

/*
.persistent-player__border--mini {
  --persistent-player-width: 60rem;
  width: var(--persistent-player-width) !important;
  height: calc(.58 * var(--persistent-player-width)) !important;
}

.persistent-player__border--mini:hover {
  opacity: .5 !important;
}
*/

.player-overlay-background {
  left: 0 !important;
}

div.tw-flex.tw-flex-nowrap.tw-full-height.tw-overflow-hidden.tw-relative > div:nth-of-type(2) {
  max-width: 15%;
}

.channel-header-user-tab__user-content .tw-nowrap {
  word-break: keep-all !important;
}

.right-column {
  /*width: 15% !important;*/
  z-index: 9999 !important;
}

.channel-root__right-column {
  width: 100% !important;
}

/* .room-selector {
  border: 1px solid #00ffe787 !important;
} */

.stream-chat {
  border: 1px solid #00ffe787 !important;
}

.tw-root--theme-dark .channel-root__right-column,
.tw-root--theme-dark .room-selector,
.tw-root--theme-dark .chat-room,
.tw-root--theme-dark .tw-border-l.tw-c-background-base.tw-flex.tw-flex-column.tw-flex-grow-0.tw-flex-nowrap.tw-flex-shrink-0.tw-full-height.tw-z-above {
  background-color: rgba(0, 0, 0, 0) !important;
  transition: .3s !important;
}

.tw-root--theme-dark .channel-root__right-column:hover,
.tw-root--theme-dark .room-selector:hover,
.tw-root--theme-dark .chat-room:hover,
.tw-root--theme-dark .tw-border-l.tw-c-background-base.tw-flex.tw-flex-column.tw-flex-grow-0.tw-flex-nowrap.tw-flex-shrink-0.tw-full-height.tw-z-above:hover {
  background-color: rgba(0, 0, 0, .2) !important;
}

.chat-room__content > .tw-z-default {
  display: none !important;
}

.chat-line__message {
  font-weight: bold !important;
  padding: 5px;
}

.tw-root--theme-dark .chat-line__message {
  background-color: rgba(15, 14, 17, 0.3) !important;
}

.tw-root--theme-dark .chat-line__username {
  border: 1px solid #424242 !important;
}
/*.text-fragment {
	border: 1px solid #424242 !important;
}*/
.room-selector__header {
  height: 25px !important;
}

.tw-c-text-alt,
tw-align-items-center,
.chat-line__message {
  font-size: 1rem !important;
}

.tw-z-default .pinned-cheer-v2 {
  visibility: hidden !important;
  height: 0 !important;
}

.chat-list {
  margin-bottom: 108px !important;
}

.chat-input {
  position: absolute !important;
  bottom: 0 !important;
  right: 0 !important;
  min-width: 286.688px !important;
  width: 100% !important;
}

.chat-input .tw-border-t {
  border: none !important;
  position: absolute !important;
  bottom: 15px !important;
  right: 72px !important;
}

.chat-rules {
  z-index: 100 !important;
}
/*
.text-fragment {
  color: var(--color-twitch-purple-10) !important;
}

.tw-root--theme-dark .text-fragment {
  color: white !important;
}
*/
/* 팔로우확인 팝업 */
.tw-absolute.tw-balloon.tw-balloon--left.tw-balloon--md.tw-balloon--up.tw-block .tw-border-b.tw-border-l.tw-border-r.tw-border-radius-medium.tw-border-t.tw-c-background-base.tw-c-text-inherit.tw-elevation-1 {
  right: 15% !important;
}

/* 버퍼링 팝업 */
.tw-absolute.tw-bottom-0.tw-mg-b-5.tw-mg-r-5.tw-right-0 {
  right: 15% !important;
}

@media (orientation: portrait) {
  .chat-room,
  .chat-room:hover,
  .tw-border-l.tw-c-background-base.tw-flex.tw-flex-column.tw-flex-grow-0.tw-flex-nowrap.tw-flex-shrink-0.tw-full-height.tw-z-above,
  .tw-border-l.tw-c-background-base.tw-flex.tw-flex-column.tw-flex-grow-0.tw-flex-nowrap.tw-flex-shrink-0.tw-full-height.tw-z-above:hover {
    background-color: #18181b !important;
  }
  
  .chat-room {
    padding-left: .8rem !important;
  }
}

}

/* i really want this to be global */
.chat__message {
    border-bottom: none;
    border-radius: 8px;
    margin: 4px 8px;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: flex-start;
    align-content: center;
    align-items: center;
    padding: 2px;
    position: relative;
}
.chat__message__content-wrapper {
    margin-left: 64px;
    width: 100%;
    background: #14182b;
    border-bottom: none;
    border-radius: 8px;
    padding: 8px 16px;
    position: relative;
}
.chat__message__content-wrapper:after {
content: '';
position: absolute;
border-style: solid;
border-width: 15px 17px 15px 0;
border-color: transparent #14182b;
display: block;
width: 0;
z-index: 1;
margin-top: -15px;
left: -17px;
top: 25%;
}
    
.chat__message__avatar {
    position: absolute;
    left: 0;
    bottom: 10px;
    top: 0;
    width: 44px;
    height: 44px;
    max-height: 44px;
    max-width: 44px;
    overflow: hidden;
    border: 2px solid rgba(255, 255, 255, .075);
}
    
.chat__message__avatar:hover {
    margin: 4px;
    height: 44px;
    width: 44px;
    max-height: 44px;
    max-width: 44px;
    overflow: hidden !important;
    border: 2px solid rgba(255, 255, 255, .075);
    border-radius: 100%;
}
.chat__message__content-wrapper:hover {
    background: #333745;
}
.chat__message__avatar:hover {
    overflow: hidden;
}
.chat__message__avatar img {
    width: 100%;
    height: 100%;
}
.chat__input {
    background: rgba(0, 0, 0, .1);
    color: #f3f3f3;
    width: 100%;
    flex: 1 1 auto;
}
.chat__submit {
    background: #f26177;
    top: 4px;
    right: 8px;
}
.chat-wrapper {
    background: linear-gradient(138deg, #080c1c 0%, #080c1c 100%);   
}
.chat__input-wrapper {
    margin-top: 8px;
    padding: 4px 8px;
    background: linear-gradient(138deg, #252938 0%, rgba(15, 18, 32, 1) 100%);
    border-top: 2px solid rgba(255, 255, 255, .25);
}
.chat__input-wrapper form {
    width: 100%;
}
.chat__message__head {
    margin-top: 4px;
}
.chat__message__user {
    color: #fff;
}
.chat__message__content {
    color: #f3f3f3;
    filter: brightness(300%);
}

