    transform: scale(1.2);
}
/*吐槽框显眼化*/
.jandan-tucao {
    z-index: 999;
    margin: 10px 0;
    padding: 10px;
    border: 3px solid #5eeaf3;
    border-radius: 9px;
    background: #e8e6e617;
    width: fit-content;
}
input {
    background: #2f2f2f;
    border: 2px solid black !important;
}
textarea {
    background: #2f2f2f;
    border: 1px solid black !important;
    color:#6f6;
}
button, #submit {
    background: #2f2f2f !important;
    border: 3px solid black !important;
    margin: auto !important;
    background-color: black !important;
    box-shadow: inset 0px 13px 10px #ffffff1c;
    color:white;
}
button:active ,#submit:active {
    transform: scale(0.95);
}
/*改变配色（反色）*/
.logo {
    filter: drop-shadow(16px 16px 20px #edea76) invert(0%) !important;
}
#content .title {
    background-color: #ffffff42;
}
.acv_author {
    background-color: #ffffff1f;
}
.hot-tabs li.current {
    background-color: #116134;
    /* font-weight: bold; */
}
/*评论框体*/
/*按钮*/
.cp-pagenavi a {
    background-color: #251e1e;
}

/*标题*/
#content .title {
    color: #ff8181;
}
#content .post .indexs {
    font-size: 15px;
    color: #47937e;
}
/*文章内容*/
#content .post p {
    color: #AED581;
}


/*外部包装框*/
#wrapper {
    padding: 0.1em;
    border: double #4e574e;
    margin: 2em auto;
}

/*作家*/
.tucao-author,
.tucao-popup-author {
    color: #ffffffbd;
    text-shadow: 0 0 1em white;
}
/*图像暗化*/
img {
    opacity: 0.7;
    background-color: black;
    filter: alpha(opacity=70);
    /* For IE8 and earlier */
    z-index: 99999;
}
* :hover {
    opacity: 1.0;
    filter: alpha(opacity=70);
    /* For IE8 and earlier */
}
img:active {
    transform: scale(1.8);
    z-index: 99999;
}
#sidebar ul {
    border-bottom: 1px solid #ffffff0f;
    line-height: 1.5em;
    background: black;
}
/*热门吐槽暗*/
.tucao-hot {
    border: 1px solid #13b4ef;
    background-color: #292e2b;
}
.tucao-hot-title {

    color: #5cc2c3;
    background-color: #257569;
}

/*搜索栏*/
#sidebar form {
    background-color: #ffffff70;
}
#sidebar form input {
    background-color: #ffffff20;
}
/*按钮*/
.wp-pagenavi a {
    background-color: #232020;
}
/*右侧的推荐栏白色该黑*/
.acv_comment {
    background: none repeat scroll 0 0 #0000;
}
/*心理咨询热线*/
#content .post blockquote {
    background-color: #c8c7cc29;
    border-left: 5px solid #6b3939;
    /* color: #d6d6d6 !important; */
    /* margin: 10px 0; */
    padding: 10px 15px 5px 20px;
}
/*文章内部的引用*/
#content .post h4.pullquote {
    background-color: #f3f3ff36;
    border-left: 5px solid #398ab3;
}
/*文章白白*/
#content .post p {
    color: #fff2d8c7;
}
html {
    background-color: #000;
}
body {
    background-color: black;
    background-attachment: fixed;
    /* 	background-image: url(https://wx3.sinaimg.cn/mw690/006rgJELly1g6639vkyiaj30b40b4jrp.jpg)!important; */
    background-image: url(http://ww1.sinaimg.cn/large/0083vuQJly1gd6dulgxkyj30b40b40di.jpg)!important;

