    background: #f5deb321;
}
/* 屏蔽广告 */
#tucao-gg {
    display: none !important;
    position: absolute;
}
.jandan-tucao-more {
    background-color: #cccccc1f;
}
#so360_submit {
    display: none;
}
.hot-tabs {
    border: none;
}
.hot-tabs .hot-tab {
    border-right: 0px;
}
.hot-tabs .current-tab {
    color: #333;
    font-weight: bold;
    background-color: #4e4e4e;
}
#content .f {
    border-bottom: 0px solid #e5e5e5;
}
}

