@-moz-document url-prefix("https://elearning.binadarma.ac.id/") {
/*
    * Name:    Binadarma Dark Mode
    * Author:  Ahmad Ansori Palembani
    * Version: 20.05r1

    Versioning info : YY.MMxxB
    Y  = Year    
    M  = Month
    xx = r  = release
         rc = release candidate
         b  = beta
         s  = snapshot
    B  = Build/Version
*/
/* colors */
:root {
    --bg: #2f3640;
    --bger: #434758;
    --bgest: #2A3039;
    --fg: #f5f6fa;
    --gray: #7f8fa6;
    --blue: #37b0ff;
    --bidarBlue: #019be1;
    --red: #e84118;
}

/* New Codes - 16.05.2020 */
.activity img.iconlarge {
    background: var(--bidarBlue);
    padding: 4px;
    border-radius: 100px;
    margin-right: 10px;
    margin-top: 2px;
}

.section li.activity, .section:hover li.activity:hover {
    background-color: var(--bger);
    border-radius: 8px;
    margin-top: 10px;
}
.section .activity .actions {
    background-color: transparent;
}
.activity .instancename{
padding-top: 8px;
    position: absolute;
}

.pagelayout-report .hidden-blocks {
    background-color: var(--bg);
}

.d-flex.align-items-center.flex-wrap,
.row-fluid.no-gutters,
.activity .togglecompletion{
    padding: 5px
}
.d-flex.paging-bar-container.mb-3 {
    padding: 5px 5px 0px 5px
}

/* header */
#wrapper {
    width: 100%;
    max-width: 100%;
    border-top: 4x solid var(--bg);
}
#page-header {
    background: var(--bg);
    border-radius: 2px;
}
.navbar,
.card {
    background: var(--bger);
}
.navbar-inner {
    background: var(--bger);
}
.navbar .btn-navbar:hover,
.navbar .btn-navbar:focus,
.navbar .btn-navbar:active,
.navbar .btn-navbar.active,
.navbar .btn-navbar.disabled,
.navbar .btn-navbar[disabled] {
    background: var(--bger);
}
.navbar .btn-navbar {
    background: var(--bg);
}
.navbar #search button[type="submit"] {
    background: var(--bg) url(https://elearning.binadarma.ac.id/theme/image.php/lambda/theme/1580986309/bg/icon-search) no-repeat center center!important;
    color: var(--fg);
}
.navbar #search button[type="submit"]:hover {
    background: var(--bger) url(https://elearning.binadarma.ac.id/theme/image.php/lambda/theme/1580986309/bg/icon-search) no-repeat center center!important;
    color: var(--fg);
}
.navbar #search input {
    background: var(--bg);
}
.navbar #search:hover input#coursesearchbox {
    background: var(--bg);
}
.navbar .brand {
    background: var(--bgest);
}
.navbar .brand:hover {
    background: var(--bgest);
}
.navbar .dropdown-menu {
    border-bottom: 4px solid var(--bg);
}
.dropdown-menu > li > a {
    color: var(--fg);
}
.navbar .nav > li > a:focus,
.navbar .nav > li > a:hover,
.navbar .nav > .active > a,
.navbar .nav > .active > a:hover,
.navbar .nav > .active > a:focus,
.navbar .nav li.dropdown.open > .dropdown-toggle,
.navbar .nav li.dropdown.active > .dropdown-toggle,
.navbar .nav li.dropdown.open.active > .dropdown-toggle {
    background: var(--bgest);
}
.navbar .dropdown-menu > li > a {
    background-image: none;
    background-color: var(--bg)!important;
    color: var(--fg)!important;
    border-bottom: transparent;
}
.navbar .dropdown-menu > li > a:hover {
    background-image: none;
    background-color: var(--bger)!important;
    color: var(--fg)!important;
}
.moodle-actionmenu .dropdown-item .menu-action-text {
    color: var(--fg);
}
.moodle-actionmenu .dropdown-divider .filler {
    border: transparent;
    background: transparent;
}
.moodle-actionmenu .dropdown-item:hover {
    background: var(--bger);
}
.moodle-actionmenu .dropdown-item .menu-action-text:hover {}
.caret {
    border-top: 4px solid var(--fg);
}
#loggedin-user .usertext {
    color: var(--fg);
}
/* body (base) */
.mr-auto,
.mx-auto,
.path-user #page-header .card {
    background-color: var(--bger);
}
.path-user #region-main .profile_tree,
.path-user #region-main .profile_tree::before {
    background: var(--bg) repeat-x scroll left top;
}
.userprofile .profile_tree section {
    border: transparent;
    background: var(--bger);
}
.profileblock .welcome_userpicture {
    border-radius: 50%;
}
img.userpicture {
    border-radius: 50%;
}
img.icon {
    vertical-align: middle;
    padding-bottom: 2px;
}
.path-user #region-main .page-context-header {
    padding: 10px;
}
body {
    background: none repeat scroll 0 0 var(--bg);
    padding-top: 0;
    color: var(--fg);
}
/* texts */
.breadcrumb > li {
    text-shadow: 0 1px 0 var(--gray);
}
.block ul.block_tree a,
.breadcrumb a,
.instancename,
.block_book_toc li a,
.block_site_main_menu li a,
.navbottom .booknext,
.navbottom .bookprev,
.navbottom .bookexit {
    color: var(--fg);
}
.breadcrumb {
    color: var(--gray);
}
/* pages */
#page {
    background: var(--bg);
}
.blockstyle-01 #region-main .block .card-title {
    background-color: var(--bger);
}
.border-bottom {
    border-bottom: 1px transparent!important;
}
.block .card-title {
    background: transparent;
}
.block .block-cards a.coursename {
    color: var(--fg);
}
.pagination ul > li > a,
.pagination ul > li > span {
    border: 1px transparent;
    background-color: var(--bger);
}
.page-link {
    color: var(--fg);
}
h1,
h2,
h3,
h4,
h5,
h6,
.back-to-top,
.socials p,
#socialheading {
    font-family: "Open Sans", sans-serif;
    font-weight: 700!important;
    color: var(--fg);
}
.block {
    background: var(--bger);
    border: transparent;
}
.block .content .userpicture {
    border-radius: 50%;
}
.card.dashboard-card {
    border: transparent;
    background: var(--bger);
}
.block .block-cards .multiline {
