}
.text-muted {
    color: var(--gray);
    padding: 0px 0px 0px 10px;
}
a,
a:visited,
a.active,
a:focus,
#page-mod-scorm-player .breadcrumb-button a {
    color: var(--fg);
}
.dropdown-menu {
    background: var(--bger);
    border: transparent;
}
#block-login div#user,
#block-login div#pass {
