    color: var(--fg);
}
#page-footer .footerlinks .socials .social_icons a.social {
    color: var(--bg);
}
/* buttons */
.dropdown-menu .dropdown-item a {
    color: var(--fg);
}
a:hover {
    color: var(--blue);
}
button.btn.m-0 {
    padding: 1px 12px 1px 12px;
}
button,
.btn,
.btn-default,
input.form-submit,
input[type="button"],
input[type="submit"],
input[type="reset"],
input.form-submit,
input#id_submitbutton,
input#id_submitbutton2,
