@-moz-document url-prefix("https://www.binadarma.ac.id/") {
/* colors */
:root {
    --bg: #2f3640;
    --bger: #434758;
    --bgest: #2A3039;
    --fg: #f5f6fa;
    --gray: #7f8fa6;
    --blue: #37b0ff;
    --bidarblue: #2271a3;
}

/* root */
body {
    color: var(--fg);
}

/* background */
.u_row.u_full_row{
    background: var(--bg)!important;
}
.container-fluid {
    background-color: var(--bg)!important;
}
.main-color-2-bg {
    background: var(--bidarblue);
}


/* 0content */

.path-login .card-header.text-center{
    color: var(--bger);
}

.item-content a {
    color: var(--blue);
}

.item-content a:hover,
#main-nav .navbar-nav > li > a .menu-description {
    color: var(--gray);
}

.btn.btn-default.btn-lighter {
    color: var(--fg);
}
}

@-moz-document url-prefix("https://sisfo.binadarma.ac.id/") {
/* --C colors */
:root {
    --bg: #2f3640;
    --bger: #434758;
    --bgest: #2A3039;
    --fg: #f5f6fa;
    --gray: #7f8fa6;
    --blue: #37b0ff;
    --bidarblue: #2271a3;
}

/* --Txt text */
.highcharts-title {
    color: var(--fg);
}
.highcharts-title {
    fill: var(--fg)!important;
}

/* --0 root */
body {
    background-color: var(--bg);
} 

/* --P panel */
.panel {
    background-color: var(--bger);
}

.panel-default {
    border-color: transparent;
}

.panel-default > .panel-heading {
    background-color: var(--bgest);
    border-color: transparent;
    background-image: linear-gradient(to bottom, var(--bgest) 0%, var(--bgest) 100%);
    color: var(--fg);
}

/* --Cnt content */
.highcharts-background {
    fill: var(--bger);
}

/* login card */
.card {
 background: var(--bg);
}

/* input textbox */ .form-control {
    padding-left:10px !important;
}

/* logos */ .input-group-addon {
    color: var(--fg);
}

/* buttons */
.login-page .login-box a {
    color: var(--blue);
}

.bg-indigo{
    background-color: var(--blue) !important;
}
}

