.markdown,
.border-gray-200,
.hover\:text-gray-600:hover {
	border-color: var(--border);
	color: var(--text) !important;
}

.text-gray-900 {
	color: var(--sidebar_text);
}

html,
body {
	background-color: var(--background);
}

.w-72,
.bg-gray-100,
.prism-code,
.form-select {
	background-color: var(--sidebar) !important;
}

.text-gray-700,
.text-gray-900 {
	color: var(--text);
}

.bg-white {
	background-color: var(--background);
	color: var(--text);
}

.bg-gray-50 {
	background-color: var(--sidebar);
	color: var(--sidebar_text);
}

.border-gray-200 {
	border-color: var(--border);
}
}

