}
blockquote p {
    color: #d2cfcf;
}
select {
    color: #fff;
}
.paginate-results.show-more{
    filter:invert(91%)
}
.show-more .btn-dark {
    filter: invert(100%);
}
.comment-body {
    border-top: 1px solid #ef172f;
}
