/*******************************/
/* leave for later 
[style*="background: url(https://play.typeracer.com/com.typeracer.guest.Guest/B7496B103318F476B179891EF1D2ED36.cache.png)" {
    background: url(); 
}*/
body {
    background: #212121;
    color: #999;
}
a {
    color: #fff;
    text-decoration: none;
}
a:hover {
    color: #bbb;
}
* {
    font-family: Roboto;
}
.main {
    background: #212121;
}
.main,
.themeContent {
    margin: auto;
    max-width: 900px;
}
.themeHeader,
.navigation {
    height: 75px !important;
    background: #191919;
}
.themeHeader h1,
.themeHeader h2,
.themeHeader h3 {
    padding: 0;
}
.container {
    background: none;
    /*background-size:120px 120px;
    background-repeat:no-repeat;*/
}
.title {
    padding-top: 10px;
    padding-left: 30px;
    width: 150px;
}
.title a {
    font-family: "DM Mono" !important;
    font-weight: 500;
    font-size: 16pt;
    text-rendering: optimizeLegibility;
}
.title a:hover {
    color: #fff;
}
.title a::after {
    content: "_";
}
.navigation {
    padding: 0;
    vertical-align: inherit;
}
div[style*="margin-bottom: 10px;"] {
    margin-bottom: 0px !important;
}
.navigation a {
    padding: 30px 15px 26px;
    font: normal 11pt Roboto !important;
    font-weight: normal;
}
.navigation a:hover,
.navHome {
    background: none !important;
    border-bottom: solid 2px #fff;
}
.navProfile,
.navProfile,
.navHistory,
.navCompetitions,
.navTexts,
.navMessages,
.navFriends,
.navUpgrade {
    background-color: transparent;
}

/* user info */
.mainUserInfoBoxWpm {
    font-size: 28pt;
    position: absolute;
    padding: 0;
    top: 14px;
    right: 15px;
    font-weight: bold;
    display: block !important;
    text-align: right;
    font-family: "Lucida Sans Unicode";
    height: 0;
    z-index: 0;
    color: #fff !important;
}
[title*="average speed info"] {
    position: absolute;
    padding: 0;
    top: 55px;
    right: 17px;
    font-size: 8pt;
    color: #ccc;
    text-decoration: none;
    text-shadow: 1px 1px 4px #191919;
    font: normal 7pt Roboto !important;
}
[title*="average speed info"]:hover {
    color: #aaa;
}
[title*="skill level info"] {
    /*    position:absolute;
    top:20px;
    right:40px;
    text-decoration:none;
    text-align:right;
    color:#888;
    font-size:8pt;*/
    display: none;
}
.mainUserInfoBox tr.headRow td + td {
    color: transparent;
}
.mainUserInfoBox td {
    color: #00000000;
    user-select: none;
}
.datarow .ImageButton,
[src*="user_edit24.cache.png"] {
    position: absolute;
    top: 8px;
    right: 6px;
    z-index: 90;
    filter: saturate(0) contrast(1.5);
    background: url(https://files.catbox.moe/b77rvs.png) no-repeat !important;
    background-size: 16px 16px !important;
}
.datarow .ImageButton:hover {
    opacity: .90;
}
.mainUserInfoBox .userNameLabel,
span.mainUserName {
    position: absolute;
    top: 8px;
    right: 33px;
    font-family: Verdana;
    font-size: 8pt;
    color: #fff;
}
.mainUserInfoBox .MainUserInfoEditor a {
    position: absolute;
    top: 40px;
    left: 30px;
    font-size: 7pt;
    text-decoration: none;
    color: #aaa !important;
    text-transform: lowercase !important;
    font: normal 9pt Roboto !important;
}

/* textarea */
textarea,
input[type=text],
select[name="n"] {
    background-color: #262626;
    border: 1px solid #444;
    font-family: "DM Mono" !important;
    font-size: 9pt !important;
    color: #fff;
}
input[type="submit"] {
    background-color: #191919;
    font-family: Roboto;
    padding: 4px 8px;
    margin: 0 5px;
    color: #bbb;
    border: 1px solid #262626;
}
table.scoresTable th {
    border: none !important;
    padding: 8px;
}

/* popup */
.trPopupDialog {
    background-color: #1a1a1a;
    border: 1px solid #383838;
    color: #ddd;
    box-shadow: 0px 0px 7px 2px rgba(25, 25, 25, 1);
}
.btnPin {
    background: url("https://files.catbox.moe/b77rvs.png") no-repeat !important;
    background-size: 16px 16px !important;
    margin-top: -2px !important;
    margin-left: 3px !important;
}
.trPopupDialog .xButton {
    background: #363636;
    box-shadow: none;
    margin-right: -0.6em;
    margin-top: -0.6em;
}
.gwt-Image {
    margin-bottom: -2px;
}
.gwt-InlineHTML,
.gwt-InlineLabel {
    color: #999;
}
.UserInfoContextMenu {
