/* Ted Race Animations */
.scoreboard .row .progressBar {
    border: none;
    margin: 0;
    margin-left: 0;
    margin-right: -400px;
    height: 4px;
    transition: all 1s linear;
    background-clip: content-box, padding-box;
    position: relative;
}
.scoreboard tr:nth-child(5n+1) .row .progressBar {
    background-image: linear-gradient(to bottom, #212121 0%, #212121 100%), linear-gradient(to bottom, #83ffa9 0%, #83ffa9 100%);
}
.scoreboard tr:nth-child(5n+2) .row .progressBar {
    background-image: linear-gradient(to bottom, #212121 0%, #212121 100%), linear-gradient(to bottom, #eded82 0%, #eded82 100%);
}
.scoreboard tr:nth-child(5n+3) .row .progressBar {
    background-image: linear-gradient(to bottom, #212121 0%, #212121 100%), linear-gradient(to bottom, #84d0f5 0%, #84d0f5 100%);
}
.scoreboard tr:nth-child(5n+4) .row .progressBar {
    background-image: linear-gradient(to bottom, #212121 0%, #212121 100%), linear-gradient(to bottom, #fa80c5 0%, #fa80c5 100%);
}
.scoreboard tr:nth-child(5n+5) .row .progressBar {
    background-image: linear-gradient(to bottom, #212121 0%, #212121 100%), linear-gradient(to bottom, #ce85f6 0%, #ce85f6 100%);
}
.avatar {
    position: absolute;
    left: 0;
    top: -20px;
}
.scoreboard .row .progressBar .avatar .nameContainer {
    width: 400px;
    text-align: left;
}
.nameContainer + img {
    display: none;
}
.lblName {
    white-space: normal !important;
    display: inline;
}
.scoreboard .row .rankPanel {
    z-index: 1;
}
.scoreboard > tbody > tr > td > table > tbody > tr > td {
    overflow: hidden;
}
.scoreboard .row .rankPanel .rankPanelWpm-self {
    font-size: 120%;
}
.mainViewport .room .gameView {
    transition: all 2s linear;
}

