.inputPanel span {
    letter-spacing: 0.25px;
    word-spacing: 2px;
    line-height: 28px;
    font-size: 22px !important;
    text-decoration: none;
    font-family: 'Roboto Mono', Consolas, monospace;
}
.txtInput {
    background: none !important;
