@-moz-document domain("muycomputer.com"), domain("muyseguridad.net") {
* a {color: #ccc !important;}
* a:hover {color: #aaa !important;}
body {background: #222 !important;color: #bbb !important;}
body.dark {background: #222 !important;color: #bbb !important;}
img {background-color: #111 !important;filter: grayscale(30%) brightness(70%) !important;}
h1, h2, h3, h4, h5, h6, h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {color: #ccc !important;}
#mvp-main-nav-top, #mvp-fly-wrap, .mvp-soc-mob-right, #mvp-main-nav-small-cont 
.mvp-blog-story-text p {color: #bbb !important;}
.mvp-inf-more-wrap a {background: #ddd !important;}
.mvp-inf-more-wrap a:hover {background: #333 !important;}
.mvp-post-title {color: #ccc !important;}
.mvp-author-info-text {color: #bbb !important;}
#mvp-content-main p {color: #bbb !important;}
#mvp-content-main p a, .mvp-post-add-main p a {color: #ccc !important;}
#mvp-content-main p a:hover, .mvp-post-add-main p a:hover {color: #333 !important;}
.mvp-post-tags-header {color: #ccc !important;background: #0c0c0c !important;}
.mvp-nav-logo-reg img {filter: grayscale(0) !important;}
#bgon-cookie-bar {background-color: #111 !important;color: #bbb !important;border: 1px solid #333 !important;}
#mvp-main-nav-bot-cont, .mvp-mega-dropdown {background-color: #111 !important;color: #bbb !important;}
#mvp-main-nav-bot-cont a, .mvp-mega-dropdown a {color: #bbb !important;}
.mvp-fly-but-wrap span {background: #bbb !important;}
.mvp-nav-search-but{color: #bbb !important;}
ul.mvp-mega-list li a, .mvp-nav-menu ul li ul.mvp-mega-list li a p {color: #bbb !important;}
#mvp-main-body-wrap {background: #222 !important;color: #bbb !important;}
span.mvp-cd-cat, span.mvp-cd-date {color: #bbb !important;}
.mvp-feat5-small-main-text h2 {color: #ccc !important;}
.mvp-feat5-small-main-text p {color: #bbb !important;}
.mvp-feat5-mid-main-text h2 {color: #ccc !important;}
.mvp-feat5-mid-main-text p {color: #bbb !important;}
.mvp-feat1-list-text h2 {color: #ccc !important;}
.mvp-feat5-mid-sub-text h2 {color: #ccc !important;}
.mvp-widget-feat2-right-text h2 {color: #ccc !important;}
.mvp-blog-story-text h2 {color: #ccc !important;}
.mvp-prev-next-text p {color: #ccc !important;}
span.mvp-author-box-name a{color: #ccc !important;}
#mvp-author-box-text p {color: #bbb !important;}
.mvp-related-text, .mvp-post-more-text p {color: #ccc !important;}
#mvp-content-main a, .mvp-post-add-main a {color: #ccc !important;}
#mvp-content-main a:hover, .mvp-post-add-main a:hover {color: #333 !important;}
#mvp-content-main a, .mvp-post-add-main a { box-shadow: inset 0 -1px 0 red !important; }
.publisher-anchor-color { color: rgb(40, 140, 228) !important; }
.publisher-anchor-color a { color: rgb(40, 140, 228) !important; }
a.publisher-anchor-color { color: rgb(40, 140, 228) !important; }
elemento {color: #bbb !important;}
  .rwp-review-wrap *, .rwp-review-wrap ::after, .rwp-review-wrap ::before {color: #bbb !important;}
}

