@-moz-document domain('youtube.com') {

ytd-live-chat-frame {
    margin: 0;
    position: absolute;
    left: 0;
    top: 50px;
    width: 450px;
    padding: 0;
    height: calc(100vh - 100px) !important;
    opacity: 0.06;
    border: none;
    z-index:1;
  }

  ytd-live-chat-frame:hover {
    opacity: .95;
    transition: .5s;
  }

  div#chat {
    position: initial;
  }

  #show-hide-button {
    margin-top: -10px;
    filter: blur(1px);
  }

  #show-hide-button:hover {
    filter: none;
    transition: .5s;
  }
  
  #item-scroller {
  scrollbar-color: #494242 #181818;
  scrollbar-width: thin;
  }

  yt-live-chat-header-renderer {
    filter: blur(2px);
  }

  yt-live-chat-header-renderer:hover {
    filter: none;
    transition: .5s;
  }
}

/* MangaHost D E L U X E | Lucas Celli */

@-moz-document domain(mangahost2.com){
body {
    font-family:'Segoe UI';
}
.ads, .ads970x90, .ads300x600, .ads728x90, .ads336x280, .ads300x250, .ads-content {
    opacity: 0;
    height: 0;
    width: 0;
    visibility: hidden;
    display: none;
}
.sidebar {
    margin-top: 7px;
}
.title-widget {
    background: #607D8B;
    padding:10px;
    margin: 0;
}
.list-featured {
    margin-top: 10px;
}
h1, h2, .bebas {
    font-family:'Segoe UI';
}
.title-widget b {
