@-moz-document url-prefix("https://3.basecamp.com") {
body {
    background: #eee;
}
.nav__bar {
    position: fixed;
    width: 100%;
    padding: 20px;
    top: 0;
    text-align: center;
    z-index: 9;
    background: /*[[nav-bg-color]]*/;
}
.nav__main {
    background-color: transparent;
    width: 100%;
}
.recording-breadcrumbs {
    width: 100%;
    max-width: 100%;
    padding: 1.5rem 0;
    background: #eee;
    border-bottom: none;
    box-shadow: none;
    top: 78px;
    position: fixed;
    z-index: 8;
}
.panel {
    margin: 80px auto;
    width: 70%;
    max-width: 100%;
    padding-top: 50px;
    background: #fff;
    border-radius: 10px;
    box-shadow: 0px 8px 9px 0 rgba(0, 0, 0, 0.1);
}
.decorated {
    color: #000;
}
.panel--perma {
    margin-top: 120px;
    box-shadow: none;
    border-radius: 10px;
    box-shadow: 0px 8px 9px 0 rgba(0, 0, 0, 0.1);
}
.perma-toolbar {
    margin-top: 0;
}
.reading__link {
    display: block;
    padding: 1rem 0 0.8rem;
    font-size: 1.6rem;
    color: #000;
}
.nav-menu__sheet {
    margin-top: 21px;
}
.break>span {
    background: #fff
}
.break--thick:before {
    border-bottom: 1px solid #ccc;
}
.thread-entry {
    background: #f7f7f7;
    padding: 40px;
}
.thread-entry__avatar {
    width: 8rem;
    left: -5.5rem;
}
.recordable {
    background: transparent;
}
.thread-entry__options {
    top: 10px
}
.readings--empty .readings__show-when-empty {
    color: #000;
}
.nav__link {
    color: #fff;
}
.nav__link--accounts {
    margin-left: 20px;
    background-image: url(http://www.dgallegos.com/downloads/Stylish/basecamp/logo-49a3492dffcd1ef0af23ef055564bb21984ad84b3c484daba2abb313c1b4b368.svg);
}
.nav__link--me {
    width: 8.6rem;
    height: 7.9rem;
    padding: 0.5rem 0.8rem 0;
    background: none;
}

.notification-status {
    right: 9.6rem;
}
.nav__link--home {
    background-image: url(http://www.dgallegos.com/downloads/Stylish/basecamp/home-c921c7a5ea43e9ca5f53878b71ceb4b4a086cfc51bb40a3e0dab5e82c20d909f.svg);
}
.nav__link--pings {
    background-image: url(http://www.dgallegos.com/downloads/Stylish/basecamp/pings-dc50fb471ed61083d8840658a9a22b49774764d2225b90aca2bfc9a94960729a.svg);
}
.nav__link--hey {
    background-image: url(http://www.dgallegos.com/downloads/Stylish/basecamp/inbox-01cf9aad826a47109f288d7f0a1ff3a9dab584abf8353526aa4754d7e532db00.svg);
}
.nav__link--activity {
    background-image: url(http://www.dgallegos.com/downloads/Stylish/basecamp/activity-73734455698459c6f2507a2e011bc3c7af6a47f81a30c2d9530c8d5b6fd87448.svg);
}
.nav__link--my-stuff {
    background-image: url(http://www.dgallegos.com/downloads/Stylish/basecamp/my-stuff-ecda53a4fff4b55d7ca244d1d67dd99ae1b3a674722dc2ebefad7fa11f3ce490.svg);
}
.nav__link--search {
    background-image: url(http://www.dgallegos.com/downloads/Stylish/basecamp/search-8fa21ccc7a917cdfc05b14d6a95b40bcc0fd6c30af0ec78c24fe90c6a298d8f4.svg);
}
span.attachment__attribute--download a {
    margin: 2px;
    border-radius: 50px;
    padding: 10px 25px;
    background: #fff;
    font-weight: bold;
    border: 1px solid #ccc;
    text-decoration: none!important;
}
span.attachment__attribute--download a:hover {
    color: #fff!important;
    background: #000;
    border-color: #000;
}
.attachment--preview .attachment__caption {
    margin-top: 2rem;
}
.metadata {
    font-size: 0.9em;
}
.action-sheet__content {
    padding: 20px;
	background-color: #000;
}
.action-sheet__action {
	opacity: .7;
}
.action-sheet__action:hover {
	opacity: 1;
}
.project-list__project {
    position: relative;
    padding: 3rem;
    margin: 5px;
    border-radius: 0.4rem;
    display: inline-block;
    width: 32%;
    background: #f7f7f7;
}
trix-editor .attachment__toolbar {
    background: #000;
}
trix-editor .attachment__toolbar:after {
    content: ' ';
    border-top-color: #000;
}
trix-editor .attachment[data-trix-mutable][data-trix-content-type~='application/vnd.basecamp.horizontal-rule.html'], trix-editor .attachment[data-trix-mutable].attachment--file .attachment__caption, trix-editor .attachment[data-trix-mutable].attachment--content .twitter-tweet, trix-editor .attachment[data-trix-mutable] img {
    box-shadow: 0 0 0 5px #000 !important;
}
.attachment:not(.attachment--preview) .attachment__caption>.attachment__size, .attachment:not(.attachment--preview) .attachment__caption .attachment__attributes {
    margin-top: 1.5em;
}
.attachment:not(.attachment--preview) .attachment__link {
    overflow: visible;
}
}

