data.css:
@-moz-document domain("gyakorikerdesek.hu") {
/*
	Általános
*/

html {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

*, *:before, *:after {
    -webkit-box-sizing: inherit;
    -moz-box-sizing: inherit;
    box-sizing: inherit;
}

body {
    padding: 0;
    font-size: 1rem;
    line-height: 1.5;
    color: #626262;
    background-color: #f8f8f8;
}

body, input, select, textarea, button {
    font-family: Nunito, Montserrat, Arial, sans-serif;
    font-weight: 400;
}

h1, h2, h3, h4, h5, h6 {
    color: #222;
}

a {
    color: #124bca;
}

input:not([type="radio"]), input:not([type="checkbox"]), select {
    height: auto;
}

input:not([type="radio"]), input:not([type="checkbox"]), select, textarea, .editor textarea {
    padding: 10px;
    font-size: .875rem;
    line-height: 1.5;
    border: 1px solid #ccc;
    color: #626262;
    border-radius: 8px;
}

input:not([type="radio"]):focus, input:not([type="checkbox"]):focus, select:focus, textarea:focus {
    outline: none;
    border-color: #124bca;
}

button:hover {
    border: 0;
}

.editor {
    margin: 0;
    padding: 0;
    border: 0;
    background: transparent;
    box-shadow: none;
}

.anonim {
    color: inherit;
    font-style: italic;
    font-weight: 400;
    opacity: .6;
}

.tovabbi {
    margin-bottom: 40px;
    text-align: right;
}

.tovabbi a,
button {
    display: block;
    padding: 10px 20px;
    border: 0;
	text-align: center;
    font-style: normal;
    font-size: 1rem;
    line-height: 1.5;
    font-weight: 700;
    color: #fff;
    background: #520f9a;
    background: -moz-linear-gradient(-45deg,  #520f9a 0%, #124bca 100%);
    background: -webkit-linear-gradient(-45deg,  #520f9a 0%,#124bca 100%);
    background: linear-gradient(135deg,  #520f9a 0%,#124bca 100%);
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
    border-radius: 8px;
    transition: all .5s;
}

.tovabbi a:hover {
    text-decoration: none;
    opacity: .7;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, .2);
}

.reklam {
    margin: 20px 0;
}

/*
	Befogó
*/

#main {
    width: 100%;
}

#main > .flex:not(.menu1) {
    width: 100%;
    min-width: 990px;
    max-width: 1300px;
    margin: 0 auto;
    padding: 0 20px;
}

#main1 {
    width: 25%
}

/*
	Fő tartalom
*/

#main2 {
    width: 75%;
    padding-top: 0;
}

#main2 > h1:nth-child(1),
#main2 > h1:nth-child(2) {
    margin: 0 0 20px;
    font-size: 2rem;
    text-align: left;
}

#main2 > br {
    display: none;
}

#main2 h2,
#main2 h3{
    margin: 20px 0 !important;
    font-size: 1.25rem;
    text-align: left;
}

#main2 h1 + div[style="text-align: center;"] {
    margin: -48px 0 30px;
    font-size: .75rem;
    font-weight: 700;
    text-transform: uppercase;
    text-align: right !important;
    color: #124bca;
}

#main2 h1 + div input {
	display: inline-block;
    vertical-align: middle;
    margin: 0 5px 0 0;
}

#main2 > h1 + .flex[style="flex-wrap: nowrap; justify-content: space-around;"] {
    margin: 0 -10px;
    flex-wrap: wrap !important;
    justify-content: flex-start !important;
}

/*
	Fejléc
*/

.menu1 {
    margin-bottom: 20px;
    padding: 15px 20px;
    line-height: 0;
    background-color: #fff;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

.menu1 input,
.menu1 select {
    height: auto;
    padding: 10px;
}

.menu1 img {
    display: inline-block;
    vertical-align: middle;
}

.menu1 a {
    transition: all .5s;
}

.menu1 a:hover {
    background-color: transparent;
    opacity: .5;
    box-shadow: none;
}

.menu1 > div:first-child {
    position: relative;
    z-index: 1;
    margin-right: 20px;
}

.menu1 > div:first-child img {
    display: block;
    height: 44px;
    margin: 0 !important;
    opacity: 0;
    /*border-radius: 8px;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .2);*/
}

.menu1 > div:first-child:after {
    position: absolute;
    display: block;
    content: "";
    z-index: -1;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: url("https://i.imgur.com/2D743IL.png") center center no-repeat;
    background-size: contain;
}

.menu1 > div:nth-child(2) {
    display: flex;
}

.menu1 > div:nth-child(2) a {
    display: block;
    height: auto;
    margin-right: 20px;
}

#keresesform .flex input {
    padding-right: 50px;
}

#keresesform .flex img {
    transition: all .5s;
}

#keresesform .flex img:hover {
    opacity: .5;
}

/*
	Oldalsáv
*/

.menu2,
.menu2:first-child .menu {
    width: 100%;
	margin-bottom: 20px;
    padding: 20px;
    background-color: #fff;
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

/*
	Profil menü
*/

.menu2:first-child #alkategoria,
.menu2:first-child #fokategoriak {
    padding: 10px;
}

.menu2:first-child {
    margin: 0;
    padding: 0;
    background: transparent;
    box-shadow: none;
}

.menu2:first-child .menu:not(#alkategoria) {
    padding: 20px 10px 10px;
    color: #fff;
    background: #520f9a;
    background: -moz-linear-gradient(-45deg,  #520f9a 0%, #124bca 100%);
    background: -webkit-linear-gradient(-45deg,  #520f9a 0%,#124bca 100%);
    background: linear-gradient(135deg,  #520f9a 0%,#124bca 100%);
}

.menu2:first-child .menu:not(#alkategoria) > div:first-child {
    position: relative;
    margin: -20px -10px 10px !important;
    padding: 20px 60px 20px 20px !important;
    border-bottom: 1px solid #fff;
    font-weight: 700;
    font-size: 1.25rem;
    box-shadow: none !important;
}

.menu2:first-child .menu:not(#alkategoria) .szint {
    position: absolute;
    top: 50%;
    right: 20px;
    width: 40px;
    height: 40px;
    line-height: 34px;
    font-size: 18px;
    font-weight: 700;
    padding: 0;
    text-align: center;
    color: #124bca;
    background: #fff;
    border-radius: 100%;
    transform: translate(0, -50%);
}

.menu2:first-child .menu:not(#alkategoria) .szint:after {
    position: absolute;
    display: block;
    content: "szint";
    top: auto;
    left: 0;
    right: 0;
    bottom: -2px;
    width: auto;
    height: auto;
    border: 0;
    padding: 2px 5px;
    font-size: 10px;
    line-height: 1;
    color: #fff;
    background: #6385ff;
    background: -moz-linear-gradient(-45deg,  #ff5337 0%, #f99a48 100%);
    background: -webkit-linear-gradient(-45deg,  #ff5337 0%,#f99a48 100%);
    background: linear-gradient(135deg,  #ff5337 0%,#f99a48 100%);
    border-radius: 10px;
    box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .1);
}

.menu2:first-child .menu:not(#alkategoria) > div:nth-child(3) {
    margin: 10px 0 10px 0;
	
}

.menu2:first-child .menu:not(#alkategoria) > div b {
    font-size: 1.25rem;
    padding: 7px 10px;
}

.menu2:first-child .menu:not(#alkategoria) > div span {
    margin: 10px 0 0 10px !important;
    padding-left: 20px;
    border-left: 1px solid rgba(255, 255, 255, .2);
    
}

.menu2:first-child .menu:not(#alkategoria) a {
    display: inline-block;
    flex: 1 0 0;
    padding: 7px 10px;
    font-size: 1rem;
    line-height: 1.5;
    color: #fff;
    transition: all .5s;
    border-radius: 8px;
}

.menu2:first-child .menu:not(#alkategoria) a:hover {
    padding-left: 15px;
    text-decoration: none;
    color: #fff;
}

/*
	Kategóriák
*/

#alkategoria,
#fokategoriak {
    padding: 0;
}

#alkategoria > div,
#fokategoriak > div {
    display: flex;
    margin-bottom: 3px;
}

#alkategoria > div:nth-child(1),
#alkategoria > div:nth-child(2),
#fokategoriak > div:nth-child(1),
#fokategoriak > div:nth-child(2) {
    padding-left: 0;
}

#alkategoria > div:nth-child(1) i,
#fokategoriak > div:nth-child(1) i {
    font-style: normal;
    font-weight: 700;
}

#alkategoria > div:nth-child(1) i a,
#fokategoriak > div:nth-child(1) i a {
    position: relative;
    padding-left: 20px;
}

#alkategoria > div:nth-child(1) i a:before,
#fokategoriak > div:nth-child(1) i a:before {
    position: absolute;
    content: "\276E";
    top: 50%;
    left: 0;
    line-height: 1;
    font-size: 1rem;
    margin-top: -.5rem;
}

#alkategoria a,
#fokategoriak a {
    display: inline-block;
    flex: 1 0 0;
    padding: 7px 10px;
    font-size: 1rem;
    line-height: 1.5;
    color: #626262;
    transition: all .5s;
    border-radius: 8px;
}

#alkategoria a:hover,
#fokategoriak a:hover {
    padding-left: 15px;
    text-decoration: none;
    color: #124bca;
}

#alkategoria .menu_kivalasztott,
#fokategoriak .menu_kivalasztott {
    background: transparent;
}

#alkategoria .menu_kivalasztott a,
#fokategoriak .menu_kivalasztott a {
    padding-left: 10px;
    color: #fff;
    background: #520f9a;
    background: -moz-linear-gradient(-45deg,  #520f9a 0%, #124bca 100%);
    background: -webkit-linear-gradient(-45deg,  #520f9a 0%,#124bca 100%);
    background: linear-gradient(135deg,  #520f9a 0%,#124bca 100%);
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .2);
}

/*
	Tag lista
*/

#main1 .menu2:nth-child(3),
.nepszeru_temak h2 {
    font-size: 1.25rem;
    font-weight: 700;
    text-align: left !important;
}

#main1 .menu2:nth-child(3) br:first-child {
    display: block;
    width: 100%;
    margin-bottom: 20px;
}

#main1 .menu2:nth-child(3) a,
.nepszeru_temak a {
    display: inline-block;
    margin: 0 -3px 5px;
    padding: .25rem .75rem;
    font-size: .75rem;
    font-weight: 400;
    color: #626262;
    background: #f0f0f0;
    border-radius: 1rem;
    transition: all .5s;
}

#main1 .menu2:nth-child(3) a:first-of-type {
    margin-top: 20px;
}

#main1 .menu2:nth-child(3) a.kiemelt,
.nepszeru_temak a.kiemelt {
    color: #fff;
    background: #999;
    box-shadow: 0 4px 10px 0 rgba(0, 0, 0, .2);
}

#main1 .menu2:nth-child(3) a:hover,
.nepszeru_temak a:hover {
    text-decoration: none;
    color: #fff;
    background: #124bca;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .2);
}

.nepszeru_temak {
    width: calc(60% - 40px);
    height: auto;
	margin: 0 10px 20px;
    padding: 20px;
    text-align: left;
    font-size: inherit;
    line-height: inherit;
    border: 0;
    background-color: #fff;
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

#main2 .nepszeru_temak h2 {
    margin-top: 0 !important;
}

.nepszeru_temak a {
    margin-left: 0;
    margin-right: 0;
}

/*
	Lábléc
*/

#siteinfo {
    padding: 60px 15px;
    font-size: 1rem;
    border: 0;
    color: #999;
    background: #fff;
}

#siteinfo a {
    display: inline-block;
    margin: 0 5px;
    font-weight: 700;
    color: #626262;
    transition: all .5s;
}

#siteinfo a:hover {
    text-decoration: none;
    color: #124bca;
}

#siteinfo span img {
    height: 10px !important;
}

/*
	Breadcrumb
*/

.morzsamenu {
    margin: 0 0 20px -5px;
    padding: 0;
}

.morzsamenu a,
.morzsamenu b {
    padding: 0 5px;
}

.morzsamenu a {
    color: #999;
    transition: all .5s;
}

.morzsamenu a:hover {
    text-decoration: none;
    color: #124bca;
}

.morzsamenu b {
    font-weight: 400;
    color: #124bca;
}

/*
	Infó doboz
*/

.gykinfo {
    display: flex;
    align-items: center;
    justify-content: center;
    height: auto;
    margin: 0 10px 20px;
    padding: 20px;
    font-size: 1.125rem;
    border: 0;
    color: #fff;
    background: #6385ff;
    background: -moz-linear-gradient(-45deg,  #ff5337 0%, #f99a48 100%);
    background: -webkit-linear-gradient(-45deg,  #ff5337 0%,#f99a48 100%);
    background: linear-gradient(135deg,  #ff5337 0%,#f99a48 100%);
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

/*
	 Kis menü
*/

.kismenu {
	display: flex;
    align-items: flex-start;
    justify-content: flex-start;
    height: auto;
    margin: 0 0 20px;
    padding: 15px 10px;
    font-size: 1rem;
    border: 0;
    color: #fff;
    background: #6385ff;
    background: -moz-linear-gradient(-45deg,  #ff5337 0%, #f99a48 100%);
    background: -webkit-linear-gradient(-45deg,  #ff5337 0%,#f99a48 100%);
    background: linear-gradient(135deg,  #ff5337 0%,#f99a48 100%);
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

.kismenu .flex {
    margin: 0;
    padding: 0 10px;
}

.kismenu .flex:hover {
    background: transparent;
    box-shadow: none;
}

.kismenu a,
.kismenu_aktiv {
    display: inline-block;
    padding: 1px 9px;
    border: 1px solid transparent;
    font-weight: 400;
    color: #fff;
    border-radius: 20px;
}

.kismenu a:hover,
.kismenu_aktiv {
    text-decoration: none;
    border-color: #fff;
    color: #fff;
}

.kismenu a {
    transition: all .5s;
}

.kismenu .kiemelt {
    font-weight: 400;
    color: inherit;
}

.kismenu img {
    display: inline-block;
    margin-top: -.25rem;
    vertical-align: middle;
    filter: brightness(1000);
}

.kismenu + div .oldalszamok {
    margin-top: 10px;
}

.kismenu + div .oldalszamok + div {
    margin-top: 10px;
    margin-bottom: 30px;
}

/*
	Kérdés lista
*/

.kerdeslista {
    position: relative;
    margin: 0 0 20px;
    padding: 20px 100px 20px 60px;
    border: 0;
    background-color: #fff;
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
    transition: all .5s;
}

.kerdeslista:hover {
    background: #fcfcfc;

}

.kerdeslista > [class*="kerdes_"] {
    position: absolute;
    top: 50%;
    left: 20px;
    margin: 0;
    padding: 0;
    transform: translate(0, -50%);
}

.kerdeslista_szoveg a {
    font-weight: 700;
    color: #222;
    transition: all .5s;
}

.kerdeslista_szoveg a:hover {
    text-decoration: none;
    color: #124bca;
}

.kerdes_alatt a {
    font-weight: 400;
    font-size: .875rem;
    color: #999;
}

.kerdeslista_valasz {
    position: absolute;
    display: flex;
    align-items: center;
    justify-content: center;
    top: 0;
    right: 0;
    bottom: 0;
    width: 50px;
    max-width: none;
    height: auto;
    margin: 0;
    padding: 0;
    font-size: 1.125rem;
    font-weight: 700;
    color: #fff;
    background: #520f9a;
    background: -moz-linear-gradient(-45deg,  #520f9a 0%, #124bca 100%);
    background: -webkit-linear-gradient(-45deg,  #520f9a 0%,#124bca 100%);
    background: linear-gradient(135deg,  #520f9a 0%,#124bca 100%);
    border-radius: 0 8px 8px 0;
}

/*
	Válasz és kérdés
*/

.kerdes,
.valasz {
	margin-bottom: 20px;
    padding: 20px;
    border: 0;
    background-color: #fff;
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

.kerdes_fejlec,
.valasz_fejlec {
    width: auto;
    padding: 20px;
    margin: -20px -20px 20px;
    font-weight: 700;
    border-radius: 8px 8px 0 0;
}

.kerdes_statusz,
.valasz_statusz {
    margin-top: 20px;
    padding: 20px 0 0;
    border-top-color: #eee;
    font-size: .875rem;
    color: #999;
}

.kerdes_statusz > div:first-child,
.valasz_statusz > div:first-child {
    margin: 0 10px 0 0;
}

/*
	Kérdés
*/

.kerdes:not([id]) .kerdes_fejlec {
    font-weight: 400;
}

.kerdes_fejlec {
    color: #fff;
    background: #6385ff;
    background: -moz-linear-gradient(-45deg,  #ff5337 0%, #f99a48 100%);
    background: -webkit-linear-gradient(-45deg,  #ff5337 0%,#f99a48 100%);
    background: linear-gradient(135deg,  #ff5337 0%,#f99a48 100%);
}

.kerdes_fejlec > div {
    font-size: .875rem;
}

.kerdes_fejlec h1 {
    margin: 0;
    font-size: 1.75rem;
    color: inherit;
}

.kerdes_kerdes {
    padding: 0 !important;
}

.kerdes_kerdes > div:first-child:not([id]) {
    width: auto !important;
    margin: -20px -20px 20px !important;
    padding: 10px 20px;
    font-size: .75rem;
    font-weight: 700;
    text-transform: uppercase;
    color: #124bca;
    background: #fcfcfc;
}

.kerdes_kerdes > div:first-child:not([id]) input {
    display: inline-block;
    vertical-align: middle;
    margin: 0 5px 0 0;
}

.kerdes_kerdes > p {
    margin: 0 0 20px !important;
}

.kerdes_kerdes > p + br,
.kerdes_kerdes + br {
    display: none;
}

.kerdes_kulcsszo {
    margin: 20px 0 0;
    font-size: .75rem;
}

.kerdes_kulcsszo > span {
    display: inline-block;
    margin: 0 0 10px 5px;
    color: #999 !important;
}

.kerdes_kulcsszo a {
    display: inline-block;
    padding: .25rem .75rem;
    color: #626262;
    background: #f0f0f0;
    border-radius: 1rem;
    transition: all .5s;
}

.kerdes_kulcsszo a:hover {
 	text-decoration: none;
    color: #fff;
    background: #124bca;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .2);
}

.kerdes_kulcsszo + .kerdes_statusz {
    margin-top: 10px;
}

/*
	Válasz
*/

.valasz_fejlec {
    color: #fff;
    background: #520f9a;
    background: -moz-linear-gradient(-45deg,  #520f9a 0%, #124bca 100%);
    background: -webkit-linear-gradient(-45deg,  #520f9a 0%,#124bca 100%);
    background: linear-gradient(135deg,  #520f9a 0%,#124bca 100%);
}

.valasz_valasz {
    padding: 0;
}

.valasz_valasz .flex > div:last-child {
    min-width: auto !important;
}

.valasz_valasz .ertek circle {
    fill: #fcfcfc;
}

.valasz_valasz .ertek path {
    stroke-width: 5px;
}

.valasz_valasz .ertek path[stroke="#B1420B"] { /* Rossz */
    stroke: #ff6868;
}

.valasz_valasz .ertek path[stroke="#1F8D25"] { /* Jó */
    stroke: #39d812;
}

.valasz_valasz .ertek text {
    font-weight: 700;
    fill: #999;
}

.valasz_valasz p {
    margin: 0 0 20px !important;
}

.valasz_valasz p:last-child {
    margin-bottom: 0 !important;
}

.valasz_valasz p + br {
    display: none;
}

/*
	Szavazás
*/

.szavazas {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    margin: 40px 0;
    padding: 20px;
    border: 0;
    background-color: #fff;
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

.szavazas > * {
    order: 10;
}

.szavazas > div:first-child {
    order: 1;
    width: 100%;
    margin: 0;
    font-size: 1.25rem;
    color: #222;
}

.szavazas .sz {
    position: relative;
    display: block;
    width: calc(50% - 10px);
    margin: 0 0 10px;
    padding: 20px 20px 20px 50px;
    background-color: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
    transition: all .3s;
}

.szavazas .sz:hover {
    color: #fff;
    background-color: #124bca;
}

.szavazas .sz input {
    position: absolute;
    top: 50%;
    left: 20px;
    margin: 0;
    padding: 0;
    transform: translate(0, -50%);
}

.szavazas .sz .sz2 {
    width: 100%;
    height: 6px;
    margin: 10px 0 0;
    background: #e1eaff;
    border-radius: 10px;
}

.szavazas .sz .sz2 span {
    height: 100%;
    background: #6385ff;
    background: -moz-linear-gradient(-45deg,  #ff5337 0%, #f99a48 100%);
    background: -webkit-linear-gradient(-45deg,  #ff5337 0%,#f99a48 100%);
    background: linear-gradient(135deg,  #ff5337 0%,#f99a48 100%);
    border-radius: 10px;
}

.szavazas > div:last-child {
    order: 1;
    width: 100%;
    margin: 0 0 20px;
    font-size: .875rem !important;
    color: #999;
}

.szavazas > div:last-child:after {
    content: " érkezett";
}

/*
	Lapozó
*/

.oldalszamok {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    margin: 30px 0 20px;
    line-height: 2.5rem;
}

.oldalszamok > a,
.oldalszamok > span {
    min-width: 2.5rem;
    margin: 0 5px 10px;
    padding: 0 5px !important;
    line-height: 2.5rem;
    font-size: .875rem;
    border: 0;
    text-align: center;
    font-weight: 400;
    border-radius: 2rem;
}

.oldalszamok > a {
    color: #666;
    transition: all .5s;
}

.oldalszamok > a:hover,
.oldalszamok a:hover:not(.active){
    text-decoration: none;
    color: #fff;
    background-color: #626262;
}

.oldalszamok > span {
    color: #fff;
    background-color: #124bca !important;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .2);
}

/*
	Kulcsszó lista
*/

.kulcsszo_lista {
    width: 100%;
	margin-bottom: 20px;
    padding: 20px 15px 10px;
    background-color: #fff;
    border-radius: 8px;
	box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

.kulcsszo_lista div {
    position: relative;
    display: flex;
    align-items: center;
    width: calc(25% - 10px);
    padding: 10px 10px 10px 40px;
    margin: 0 5px 10px;
    background: #f8f8f8;
    border-radius: 8px;
}

.kulcsszo_lista div input {
    position: absolute;
    top: 50%;
    left: 15px;
    margin: 0;
    transform: translate(0, -50%);
}

.kulcsszo_lista div a {
    transition: all .5s;
}

.kulcsszo_lista div a:hover {
    text-decoration: none;
    color: #222;
}

/*
	Toplista
*/

.toplista {
    background: #fff;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
    transition: all .5s;
}

.toplista:hover {
    background: #eee;
}

.toplista > div {
    padding: 10px 5px;
}

/*
	Figyelmeztetés
*/

.figyelmeztetes {
    margin-bottom: 20px;
    padding: 20px;
    color: #fff;
    background: #bb0101;
    background: -moz-linear-gradient(-45deg,  #bb0101 0%, #ea4040 100%);
    background: -webkit-linear-gradient(-45deg,  #bb0101 0%,#ea4040 100%);
    background: linear-gradient(135deg,  #bb0101 0%,#ea4040 100%);
    border-radius: 8px;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

/*
	Új kérdés
*/

#ujkerdes_menu {
    margin: 0;
}

#ujkerdes_menu span {
    height: auto;
    padding: 10px;
    line-height: inherit;
    background: #f8f8f8;
    border-radius: 8px 8px 0 0;
    box-shadow: none;
}

#ujkerdes_menu span[style] {
    color: #124bca;
    background: #fff !important;
}

.ujkerdes {
    margin-bottom: 20px;
    padding: 20px;
    background: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
}

.fokatvalaszt {
    border: 0;
}

/*
	Üzenetek
*/

.uzenetlista,
.uzenetlista .uzenet{
    margin-bottom: 20px;
    padding: 10px 20px;
    background: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .05);
    transition: all .5s;
}

.uzenetlista:hover {
    background: #f8f8f8;
}

}

