   margin-top: 12px !important;
}

}

@-moz-document domain("soundcloud.com") {
:root {
--color-bg-main:hsla(0,0%,0%,1.0);
--color-bg-main-transparent:hsla(0,0%,0%,0.0);
--color-txt-dark:#555;
--color-border:#333;
--color-txt-main:#878787;
--color-txt-bright:#ddd;
--color-orange:#f50;
}

html,body {
color:var(--color-txt-dark);
background:var(--color-bg-main);
background-color:var(--color-bg-main);
}

.paging-eof::before {
background:url(https://i.imgur.com/RqMK7YS.png) 50% no-repeat;
}

.ownActivity.large .ownActivity__user {
border-right-color:var(--color-bg-main);
}

.modal.modalWhiteout {
background-color:rgba(0,0,0,0.7);
}

.g-nav-item > .g-nav-link {
color:vaR(--color-txt-main);
}

.g-modal-title-h1 {
color:var(--color-txt-main);
border-color:var(--color-border);
}

.volume__sliderWrapper::before {
border-color:transparent transparent var(--color-border) var(--color-border);
}

.volume__sliderWrapper::after {
border-color:transparent transparent var(--color-bg-main) var(--color-bg-main);
}

.commentForm.m-active .commentForm__wrapper,.commentForm__wrapper {
background-color:var(--color-bg-main);
border-color:var(--color-bg-main);
}

.sc-tag {
background-color:var(--color-txt-dark);
border-color:transparent;
}

.truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after,.truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after {
background:linear-gradient(var(--color-bg-main-transparent),var(--color-bg-main));
}

.systemPlaylistBannerItem__username.sc-font {
color:rgba(255,255,255,0.7);
}

.g-tabs-link:hover {
color:var(--color-txt-main);
border-color:var(--color-txt-main);
}

.g-tabs-link.active,.g-tabs-link.active:hover {
color:var(--color-orange);
border-color:var(--color-orange);
}

.playbackSoundBadge__titleLink:focus {
color:inherit;
}

.sc-button {
background-color:var(--color-bg-main);
border-color:var(--color-border);
color:var(--color-txt-main);
}

.trackItem:not(.m-disabled).active .trackItem__additional,.trackItem:not(.m-disabled).hover .trackItem__additional {
background:linear-gradient(90deg,var(--color-bg-main-transparent),var(--color-bg-main) 17px);
}

a.sc-link-light:hover {
color:var(--color-txt-bright);
}

.compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional,.compactTrackListItem.clickToPlay.active .compactTrackListItem__additional {
background:linear-gradient(90deg,var(--bg-color-main-transparent),var(--bg-color-main) 17px);
}

.sc-border-light-top {
border-top-color:var(--color-border);
}

.playControls__bg,.playControls__inner {
background-color:var(--color-bg-main);
border-top-color:var(--color-border);
}

.soundBadge__additional {
background:linear-gradient(90deg,hsla(0,0%,0%,.1),var(--color-bg-main) 17px);
}

.listenEngagement {
box-shadow:none;
border-color:var(--color-bg-main);
}

p,.sc-type-light,a.sc-ministats,a.sc-link-light,a.sc-link-dark:hover,a.sc-link-medium:hover,.soundContext__line {
color:var(--color-txt-main);
}

input,.searchTitle {
background-color:var(--color-bg-main);
color:var(--color-txt-main);
}

.compactTrackList__moreLink:focus,.compactTrackList__moreLink:hover,.g-modal-section,.modal__modal,.tabs__tabs,.readMoreTile__countWrapper,.volume__sliderWrapper,.commentItem.m-creatorComment,.dropbar__content,.header,.l-container,.compactTrackListItem.clickToPlay.active,.compactTrackListItem.clickToPlay:focus,.compactTrackListItem.clickToPlay:hover,.sound__soundActions {
background-color:var(--color-bg-main);
}

.image__whiteOutline .image__full,.sc-input,.sc-border-light {
border-color:var(--color-border);
}

.searchTitle__text,.collection.m-overview .collection__section:not(:last-child),.g-tabs,.mixedSelectionModule,.sc-border-light-bottom {
border-bottom-color:var(--color-border);
}

.dialog,.dialog__arrow,.volume.expanded .volume__sliderWrapper,.commentForm__input,.sc-background-light {
background-color:var(--color-bg-main);
border-color:var(--color-border);
}

.textfield__clear,.textfield__clear:hover,.trackItem.active,.trackItem.hover {
background-color:transparent;
}

.playbackSoundBadge__titleLink,.playbackSoundBadge__titleLink:visited,a.playbackSoundBadge__lightLink:focus,a.playbackSoundBadge__lightLink:hover,a.sc-ministats:hover,a.sc-link-dark {
color:var(--color-txt-dark);
}

.playbackSoundBadge__titleLink:hover,.sc-button-selected {
color:var(--color-orange);
}

.l-listen-wrapper .l-about-rows,.sc-border-light-right {
border-right-color:var(--color-border);
}
}
