span#title.style-scope, .title.ytd-guide-entry-renderer, h2.ytd-simple-menu-header-renderer, #label.ytd-compact-link-renderer, #subtitle.ytd-compact-link-renderer, #name.ytd-page-introduction-renderer, #title.ytd-item-section-header-renderer, #title.ytd-page-introduction-renderer, #title.ytd-settings-options-renderer, #card-text.yt-card-item-text-renderer[text-style="CARD_ITEM_TEXT_STYLE_DISPLAY_1"], #label.ytd-toggle-theme-compact-link-renderer, a.yt-formatted-string, #upnext.ytd-compact-autoplay-renderer, yt-formatted-string.ytd-menu-service-item-renderer, yt-formatted-string.ytd-menu-navigation-item-renderer, #account-name.ytd-active-account-header-renderer, #channel-title.ytd-account-item-renderer, paper-item.list-item.ytd-account-settings{
