#navbar-search-btn
  {
    background: #5697DF !important;
    border: 1px solid #5697DF;
  }

  body
  {
    background: transparent!important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
