  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: rgba( 214, 234, 248, 0.8);
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(241, 132, 237, 0.5);
