@-moz-document regexp(".*sensescans.com/.*") {
body, .list, .large, .topbar, .dropdown{
	background: none!important;
	background: rgb(16,16,16)!important;
	color: white;
	text-shadow: none;
	-webkit-box-shadow: none!important;
	font-family: Segoe UI !important;
}
.sidebar{
	display: none;
}
.list > .title, .large > .title, #header{
	-webkit-box-shadow: none;
}
#header #title{
	background: url(http://forums.sensescans.com/Themes/ProRed2/images/theme/logo.png)no-repeat;
	background-size: 100%;
	height: 40px;
	width: 65px;
	margin-top: 5px;
	margin: 5px 10px 0px 10px;
	color: transparent;
}
.list .group > .title{
	color: rgb(0,155,254);
	border: none;
}
#page .inner > a > img{
	width: 100%;
	height: 100%;
}
::-webkit-scrollbar {
	width: 10px!important;
	height: 7px!important;
}
::-webkit-scrollbar-thumb {
	cursor: pointer!important;
	background: rgba(51,51,51)!important;
}
ul.dropdown::-webkit-scrollbar{
	display: none!important;
}
#content > div.panel > div > div:nth-child(1) > div.topbar_left > div:nth-child(3){
	width: 700px;
}
#content > div.panel > div > div:nth-child(1) > div.topbar_left > div:nth-child(3) > ul{
	width: 688px;
}
.list > .title, .large > .title{
  font-weight: 700;
}
.ads.banner{
	display: none!important;
}
.prevnext .gbutton{
	border: 1px solid #999;
}
}

