/***
Peopleware (Pplware) - NtmStyle (v2.071) ??:?? ??-??-2012
by: Newtomic
http://userstyles.org/styles/41734
***/
@-moz-document domain("pplware.sapo.pt") {
/* SAPO (barra de nav + ads) > KILL */
#bsu_container, IFRAME[name="saw_frame"] {
	display: none !important;
}
/* Secção post/artigo (Start) */
/* Body = puxar cima e esquerda > opt min 1024x??? */
BODY {
	margin-top: -38px !important;
	margin-left: -20px !important;
}
/* items menu + pesquisar = puxar cima */
LI.page_item {
	margin-top: -11px !important;
	overflow: hidden !important;
}
#search-form {
	margin-top: 0 !important;
}
/* menu = puxar cima + largura + outros */
#menu {
	margin-top: 72px !important;
	margin-bottom: -34px !important;
	overflow: hidden !important;
	width: 985px !important;
	background: none !important;
	background-color: #2662AA !important;
}
/* linha de espaço vertical pré post + sidebar = KILL */
#content-top.top-alt, #sidebar {
	display: none !important;
}
/* breadcrumbs nav = puxar cima + largura + outros */
#breadcrumbs {
	margin-top: -3px !important;
	margin-left: -1px !important;
	padding-top: 0 !important;
	padding-bottom: 0 !important;
	width: 925px !important;
	background-color: #F8F8F8 !important;
}
/* POST/ARTIGO = novo espaço interior + largura + tamanho letra maior */
.entry.clearfix {
	margin-left: -1px !important;
	padding: 5px !important;
	width: 975px !important;
	background-color: #FFF !important;
	font-size: 130% !important;
}
/* esconder fundo, não adequado para este style */
#content.clearfix.content-alt {
	background: none !important;
}
/* post titulo + titulos home page = negrito + espaços */
H1.title, H2.title {
	margin-top: -3px !important;
	padding: 0 0 5px 0 !important;
	font-weight: 900 !important;
}
/* post, autor + data = espaços */
P.post-meta {
	margin-bottom: 4px !important;
	padding: 0 !important;
}
/* links fim post (redes sociais e outros) > KILL */
CENTER[style="border: 1px solid rgb(235, 235, 235);"] {
	display: none !important;
}
/* paginação home = fix */
.entry.page-nav.clearfix {
	margin-bottom: 5px !important;
	background-color: #FFF !important;
}
.pagebar {
	position: relative !important;
	top: -13px !important;
}
/* Secção post/artigo (End) */
/* shity things = KILL */
.hr-separator, #content-bottom.bottom-alt, A[href="#logohover"], CENTER[style="border-bottom: 1px solid rgb(235, 235, 235);"] {
	display: none !important;
}
/* Secção comentários (Start) */
/* COMENTÁRIOS = novo espaço interior + largura + tamanho letra maior */
#comment-wrap {
	margin: -30px 0 0 -1px !important;
	padding: 5px !important;
	width: 975px !important;
	background-color: #FFF !important;
	border-top: 3px solid #EBEBEB !important;
	font-size: 115% !important;
}
/* titulos comentários > inicio + escrever = espaço */
H3#comments {
	padding: 5px 0 0 0 !important;
}
/* cometário container = border + cor fundo + espaço */
.comment-body.clearfix {
	margin-top: -5px !important;
	margin-bottom: -9px !important;
	padding: 2px !important;
	overflow: hidden !important;
	border: 1px solid #3D6796 !important;
	background-color: #EEE !important;
}
/* comentário data = espaço */
.comment-meta.commentmetadata {
	margin: -4px 0 -11px 0 !important;
}
/* comentário texto todo = largura máxima usável + espaço */
.comment-wrap {
	margin: -5px 0 -3px 3px !important;
	width: 89% !important;
}
/* comentário texto escrito = largura máxima usável + espaço */
.comment-content {
	margin: 8px 0 -1px -38px !important;
	padding: 0 !important;
	width: 112.7% !important;
}
/* comentários responder = re-posicionar + semi-transparente + outros (não está bonito mas poupa espaço vertical à bruta) */
.reply-container {
	position: relative !important;
	bottom: 36px !important;
	left: 46px !important;
	margin-bottom: -1000px !important;
	opacity: 0.5 !important;
}
/* comentários hierarquias = eliminar espaço */
LI.comment.odd.alt, LI.comment.even {
	margin: 0 !important;
	padding: 0 !important;
	border: none !important;
}
/* avatar img = novo tamanho 32x32 + border 1px */
IMG.avatar.avatar-57.photo {
	width: 32px !important;
	height: 32px !important;
	border: 1px solid #000 !important;
}
/* avatar container = espaço */
.avatar {
	padding: 0 !important;
}
/* avatar border original = KILL */
SPAN.overlay {
	display: none !important;
}
/* Secção comentários (End) */
/* escrever comentário > small input boxes = largura + espaços */
INPUT#author, INPUT#email, INPUT#url {
	color: #000 !important;
	width: 63% !important;
	margin: 0 !important;
	padding: 1px !important;
}
/* escrever comentário autor = aumentar espaço cima */
INPUT#author {
	margin-top: 5px !important;
}
/* escrever comentário ESCREVER = largura + espaço linhas + pequeno qd inactivo + grande qd activo */
TEXTAREA#comment {
	margin: 0 !important;
	padding: 0 !important;
	color: #000 !important;
	width: 99.9% !important;
	height: 3em !important;
	line-height: 90% !important;
}
TEXTAREA#comment:focus {
	height: 30em !important;
}
/* sub-escrever próximos comentários = negrito + vermelho */
P.subscribe-to-comments {
	margin: -5px 0 -10px 0 !important;
	color: #F00 !important;
	font-weight: 900 !important;
}
/* aviso = letra mais pequena */
SPAN.category {
	position: relative !important;
	top: 10px !important;
	font-size: 7pt !important;
}
}

