@-moz-document url-prefix("https://eva.unapec.edu.do/moodle/") {
/*
Dark Mode for EVA moodle
Ver 1.7.0 beta
ScyDact 2020Â®
*/
@font-face {
font-family: 'Roboto';
src: url('https://cdn.jsdelivr.net/gh/google/roboto@master/src/hinted/Roboto-Regular.ttf');
}
@font-face {
font-family: 'Roboto Condensed';
src: url('https://cdn.jsdelivr.net/gh/google/roboto@master/src/hinted/RobotoCondensed-Regular.ttf');
}
@font-face {
font-family: 'Noto Sans';
src: url('https://cdn.jsdelivr.net/gh/googlefonts/noto-fonts@master/unhinted/NotoSans/NotoSans-Regular.ttf');
}


/* PALETTE */
:root {
/* OLD
--back1: #1a1a1a;
--back2: #202020;
--back3: #292929;
--back4: #303030;
--back5: #393939;*/

--back1: #181b20;
--back2: #1f232a;
--back3: #262b33;
--back4: #2b323b;
--back5: #333a45;

--text1: #fafafa;
--text2: #d7d7d7;
--text3: #c7c7c7;
--text4: #b0b0b0;
--text5: #9e9e9e;

--accent: var(--blue);
--accentF2: var(--blueF1);
--accentF1: var(--blueF2);
--accentB2: var(--blueB2);
--accentB1: var(--blueB1);

--link: var(--blueB2);
/*#758bff;*/
--border: rgba(255, 255, 255, 0.3);
--borderL: rgba(255, 255, 255, 0.15);

/* UNAPEC */
--unapecBlue1: #022646;
--unapecBlue2: #02335E;
--unapecBlue3: #023f75;
--unapecBlue4: #0058a6;

/* Transparencies */
--bAlpha: 0, 0, 0;
--fAlpha: 255, 255, 255;

/* Colors */
--red: #f44336;
/*500*/
--redF1: #ffcdd2;
/*100*/
--redF2: #ef9a9a;
/*200*/
--redB1: #b71c1c;
/*900*/
--redB2: #d32f2f;
/*700*/
--orange: #ff9800;
--orangeF1: #ffe0b2;
--orangeF2: #ffcc80;
--orangeB1: #e65100;
--orangeB2: #f57c00;

--yellow: #ffeb3b;
--yellowF1: #fff9c4;
--yellowF2: #fff59d;
--yellowB1: #f57f17;
--yellowB2: #fbc02d;

--green: #4caf50;
--greenF1: #c8e6c9;
--greenF2: #a5d6a7;
--greenB1: #1b5e20;
--greenB2: #388e3c;

--blue: #2195f3;
--blueF1: #bbdefb;
--blueF2: #90caf9;
--blueB1: #0d47a1;
--blueB2: #1976d2;

--indigo: #3f51b5;
--indigoF1: #c5cae9;
--indigoF2: #9fa8da;
--indigoB1: #1a237e;
--indigoB2: #283593;

--purple: #9c27b0;
--purpleF1: #e1bee7;
--purpleF2: #ce93d8;
--purpleB1: #4a148c;
--purpleB2: #7b1fa2;

/* Non color variables*/
--borderradius: 10px;
--uiborderradius: 5px;
--shadow: 0px 0px 12px rgba(0, 0, 0, 0.4);
--shadowcard: 0px 0px 8px rgba(0, 0, 0, 0.1);
}
/* Custom Scrollbar*/
::-webkit-scrollbar {
/* width */
width: 10px;
height: 10px;
}
::-webkit-scrollbar-track {
/* Track */
background: rgba(128, 128, 128, 0.5);
box-shadow: inset 0px 0px 5px rgba(0, 0, 0, 0.3);
border-radius: 100px;
}
::-webkit-scrollbar-thumb {
/* Handle */
border: 0px solid rgba(0, 0, 0, 0);
box-shadow: inset 0px 0px 4px rgba(0, 0, 0, 0.1);
background-clip: padding-box;
background-color: var(--text4);
border-radius: 100px;
}
::-webkit-scrollbar-corner {
background: transparent;
}

body::-webkit-scrollbar {
/* width */
width: 10px;
height: 10px;
}
body::-webkit-scrollbar-track {
border-radius: 0px;
background: transparent;
}
body::-webkit-scrollbar-thumb {
/* Track */
border-radius: 3px;
}




/* MAIN BODY*/
body {
font-family: 'Roboto', sans-serif;
}
h1,
h2,
h3 {
font-family: 'Noto Sans', sans-serif;
}

html,
body,
textarea {
background-color: var(--back1);
color: var(--text1);
}

.block {
background-color: var(--back2);
color: var(--text1);
border: 0px;
}

/* Added on May 2020 */
.bg-white {background-color: var(--back1) !important;}
.bg-light {background-color: rgba(var(--fAlpha),0.01)!important;}
.bg-secondary {background-color: rgba(var(--fAlpha),0.1)!important}
.border {border: 1px solid var(--border) !important;}

/*.breadcrumb, */
#page-enrol-users #filterform,
.que .history,
.groupinforbox,
.userprofile .profile_tree section,
.well,
.card,
#region-main {
background-color: var(--back2);
color: var(--text1);
border: none;
box-shadow: var(--shadowcard)
}

.card,
#region-main {
border-radius: var(--borderradius);
}

.card-body .card-body,
#region-main .card-body {
background-color: var(--back3);
border-radius: var(--borderradius);        
}

#box_block_float .block {
height: auto;
width: 500px;
}

a:link,
a:hover,
a:visited,
a:active,
span.flagged-tag a {
color: var(--link);
}

/*
.block .header h2 {
    display: block;
    padding: 3px 15px;
    font-weight: 700;
    line-height: 20px;
    color: violet;
    text-shadow: 0 1px 0 rgba(255,255,255,.5);
    text-transform: uppercase;
    font-size: 1.1em;
