/* ################# OTHER UI ELEMENTS ################# */
/* Drop down menus (E.G. When you click your account pic)*/
.dropdown-divider {
border-top-color: var(--borderL);
}
.dropdown-menu {
background-color: var(--back1);
border: 1px solid var(--border);
border-radius: var(--borderradius);
    box-shadow: var(--shadowcard);
}
.dropdown-item,
a.dropdown-item {
color: var(--text2);
}

.dropdown-item:hover,
.dropdown-item:focus {
color: var(--text1);
text-decoration: none;
background-color: var(--back4)
}


/* -- Notifications -- */
.navbar .count-container {
/* Little notification number*/
border-radius: 10000px;
height: 1.5em;
width: 1.5em;
text-align: center;
left: 17px;
top: 1px
}
.popover-region-toggle::before,
.popover-region-toggle::after {
/*Remove little arrow*/
visibility: hidden;
}
.popover-region-container {
/* Whole box*/
background-color: var(--back1);
border: 1px solid var(--border);
box-shadow: var(--shadow);
border-radius: var(--borderradius);
}
.content-item-container {
/*Single item*/
border-bottom: 1px solid var(--borderL);
margin: 2px 0px 0px 0px;
padding-left: 10px;
padding-right: 10px;
padding-bottom: 20px;
padding-top: 5px;
}
.content-item-container:hover {
background-color: var(--back2);
}
a.content-item-container {
color: var(--text1);
}
.content-item-container .content-item-footer .timestamp {
color: var(--text4);
}
.content-item-container .view-more {
bottom: 10px;
right: 10px;
}
.popover-region-header-container {
/*Header 'Notificaciones'*/
border-bottom: 1px solid var(--border);
margin: 5px;
}
.popover-region-footer-container {
/* Footer 'Verlo todo'*/
background-color: var(--back1);
border-top: 1px solid var(--borderL);
border-bottom: 1px solid var(--border);
border-radius: var(--borderradius);
box-shadow: var(--shadow);
font-family: 'Noto Sans', sans-serif;
}
.popover-region-footer-container:hover {
background-color: var(--back2);
}

.content-item-container.unread {
/* Unread notifications*/
background-color: var(--back3);
}

.popover-region-header-container a:hover {
/* Config and confirm buttons hover*/
color: var(--accentB1);
}

/* -- Calendar -- */
.calendar_event_course {
background-color: var(--blueB2)!important;
}
.calendar_event_site {
background-color: var(--greenB2)!important;
}
.calendar_event_category {
background-color: var(--purpleB2)!important;
}
.calendar_event_group {
background-color: var(--yellowB2)!important;
}
.calendar_event_user {
background-color: var(--redB2)!important;
}

.day > a {
/* link colors inside calendar */
color: rgba(var(--fAlpha), 80%);
}
.block .minicalendar tbody tr {
color: rgba(var(--fAlpha), 70%);
}
.block .minicalendar td.weekend {
color: rgba(var(--fAlpha), 30%)!important;
}
.block .minicalendar td.today {
border: 2px solid var(--text1);
font-weight: bold;
}
.calendar_filters > ul > li > a > span > i {
/* "Clase de eventos" */
color: rgba(0, 0, 0, 55%);
}

.popover-header,
.popover-body {
/* Calendar event on hover popup */
background-color: var(--back1);
border-bottom: 1px solid var(--borderL);
color: var(--text1)
}
.popover {
background-color: var(--back1);
border: 1px solid var(--border);
}


/* Pagination (E.g. When using search bar from home)*/
.page-item.active .page-link {
background-color: var(--accent);
border-color: var(--border);
color: white;
}

.page-item.active .page-link {
background-color: var(--accent);
border-color: var(--border);
color: white;
}
page-link,
.page-item.disabled .page-link,
a.page-link {
background-color: var(--back1);
color: var(--text1)
}
a.page-link:hover {
background-color: rgba(var(--fAlpha),0.1);
color: var(--text1)
}

/* Loading pages overlay color */
@keyframes loadingOverlayPulse {
from {
background-color: rgba(0, 0, 0, 50%);
}
to {
background-color: rgba(0, 0, 0, 15%);
}
}
.overlay-icon-container {
/* e.g. loading calendar*/
background-color: transparent;
border-radius: var(--borderradius);
animation: loadingOverlayPulse 0.7s ease-in-out alternate-reverse infinite;
backdrop-filter: blur(2px);
}


/* ################# PROFILE ################# */
.userprofile .profile_tree section {
background-color: var(--back3);
border-radius: var(--borderradius);
}
.block .block-controls .dropdown-toggle {
/* Modify controls on profile page*/
color: white;
}




/* ################# EXAMS AND QUIZES ################# */
.formulation {
border-radius: var(--borderradius);
}

.formulation input {
/* Input is now white cuz background is light,
Also, allow overflow please!*/
background-color: white;
color: #333;
}
.formulation input:hover,
.formulation input:focus {
background-color: white;
color: black;
}

.que .info {
background-color: var(--back5);
border-radius: var(--borderradius);
}

.que .info .editquestion img,
.que .info .questionflag img,
.que .info .questionflag input {
filter: brightness(3) saturate(3) hue-rotate(60deg);
}

.path-mod-quiz #mod_quiz_navblock .qnbutton {
background-color: var(--back1)
}


/* Traffic light */
.path-mod-quiz #mod_quiz_navblock .qnbutton {
color: var(--text1);
padding-top: 2px
}
.path-mod-quiz #mod_quiz_navblock .qnbutton .thispageholder {
border: 1px solid var(--border)
}
/* Neutral */
.path-mod-quiz #mod_quiz_navblock .qnbutton.notyetanswered .trafficlight,
.path-mod-quiz #mod_quiz_navblock .qnbutton.invalidanswer .trafficlight,
.path-mod-quiz #mod_quiz_navblock .qnbutton .trafficlight {
background-color: transparent;
}

/* Blocked */
.path-mod-quiz #mod_quiz_navblock .qnbutton.blocked .trafficlight {
background-color: var(--redF1)
}

/* Green */
.path-mod-quiz #mod_quiz_navblock .qnbutton.correct .trafficlight {
background-color: var(--green);
}

/* Red */
.path-mod-quiz #mod_quiz_navblock .qnbutton.notanswered .trafficlight,
.path-mod-quiz #mod_quiz_navblock .qnbutton.incorrect .trafficlight {
background-color: var(--red);
}

/* Orange */
.path-mod-quiz #mod_quiz_navblock .qnbutton.partiallycorrect .trafficlight {
background-color: var(--orange)
}

/* Gray but now blue (was #868e96) */
.path-mod-quiz #mod_quiz_navblock .qnbutton.complete .trafficlight,
.path-mod-quiz #mod_quiz_navblock .qnbutton.answersaved .trafficlight,
.path-mod-quiz #mod_quiz_navblock .qnbutton.requiresgrading .trafficlight {
background-color: var(--blue);
}

/* Not graded homeworks */
.path-mod-assign td.submissionnotgraded,
.path-mod-assign div.submissionnotgraded {
color: var(--text2);
background-color: var(--back2);
}

/* Exams results */
table.quizreviewsummary td.cell,
table.quizreviewsummary th.cell{
background-color: var(--back2);
}


/* ################# COLOR FOR ASSIGNMENTS / ERRORS ################# */
.path-mod-assign td.submissionstatus,
.path-mod-assign div.submissionstatus,
.path-mod-assign a:link.submissionstatus {
    color: var(--text1);
    background-color: rgba(var(--bAlpha), 0.2);
}

/* Green */
.path-mod-assign td.submissionstatussubmitted,
.path-mod-assign div.submissionstatussubmitted,
.path-mod-assign a:link.submissionstatussubmitted,

.path-mod-assign td.submissiongraded,
.path-mod-assign div.submissiongraded,

.path-mod-assign td.earlysubmission,
.path-mod-assign div.earlysubmission {
color: var(--text1);
background-color: var(--green)
}

/* Red */
.path-mod-assign td.latesubmission,
.path-mod-assign a:link.latesubmission,
.path-mod-assign div.latesubmission,

.alert-danger,
#page-admin-index .adminerror,
.environmenttable .error,
#plugins-check-page #plugins-check .status-missing td,
#plugins-check-page #plugins-check .status-downgrade td,
#plugins-check-page .pluginupdateinfo.maturity50,
#plugins-control-panel .pluginupdateinfo.maturity50,
#adminsettings span.error,
#page-backup-restore .filealiasesfailures {
color: var(--text1);
background-color: var(--red)
}

.alert-danger a,
#page-admin-index .adminerror a,
.environmenttable .error a,
#plugins-check-page #plugins-check .status-missing td a,
#plugins-check-page #plugins-check .status-downgrade td a,
#plugins-check-page .pluginupdateinfo.maturity50 a,
#plugins-control-panel .pluginupdateinfo.maturity50 a,
#adminsettings span.error a,
#page-backup-restore .filealiasesfailures a {
/* Fix links in errors*/
color: var(--redF1);
}

/* Information Blue (Light) */
.alert-info,
#page-admin-index .releasenoteslink,
#adminsettings .form-overridden,
.que .formulation,

#plugins-check-page .pluginupdateinfo,
#plugins-control-panel .pluginupdateinfo,

span.editinstructions,
#dndupload-status {
color: var(--blueB1);
background-color: var(--blueF1)
}

/* Alert warning (yellow) */
.alert-warning,
#page-admin-index .adminwarning,
.uninstalldeleteconfirmexternal,
.que .outcome,
.que .comment {
  color: var(--text1);
  background-color: var(--yellowB2);
  border-color: transparent;
}
.alert-warning hr,
#page-admin-index .adminwarning hr,
.uninstalldeleteconfirmexternal hr,
.que .outcome hr,
.que .comment hr {
  border-top-color: transparent;
}
.alert-warning .alert-link,
#page-admin-index .adminwarning .alert-link,
.uninstalldeleteconfirmexternal .alert-link,
.que .outcome .alert-link,
.que .comment .alert-link {
    color: var(--yellowB2);
}

/* ################# DOWNLOAD WINDOW STUFF ################# */
.filepicker-filelist .filepicker-container,
.filemanager.fm-noitems .fm-empty-container {
/*Empty*/
border: 2px dashed var(--text1);
border-radius: var(--borderradius);
}
.filepicker-filelist .dndupload-target,
.filemanager-container .dndupload-target {
/*With something being held*/
background: var(--back1);
border: 2px dashed var(--blue);
border-radius: var(--borderradius);
}

.filepicker-filelist.dndupload-over .dndupload-target,
.filemanager-container.dndupload-over .dndupload-target {
/*Something on top*/
background: var(--back1);
border: 2px dashed var(--green);
border-radius: var(--borderradius);
}

.fitem.disabled .filepicker-filelist,
.fitem.disabled .filemanager-container {
/* Disabled? */
background-color: var(--back2);
color: var(--text4);
}

/* File manager table*/
.filemanager .yui3-datatable-header {
background: var(--back4) !important;
color: var(--text1) !important;
text-shadow: none;
}

.filemanager .yui3-datatable-even .yui3-datatable-cell {
background-color: var(--back3) !important;
}

/* Fix for download card formatting */
div.filemanager {
border: white;
padding: 10px;
border-radius: var(--borderradius);
box-shadow: var(--shadowcard);
background: var(--back3)
}
.filemanager .card {
box-shadow: none;
background: transparent;
}

/* Progress bar (when file is uploading) */
.dndupload-progress-outer {
background-color: var(--back1);
}

/* Upload arrow replacement */
.dndupload-arrow {
/* Upload file image*/
background-image: url("data:image/svg+xml,%3Csvg width='24' height='24' xmlns='http://www.w3.org/2000/svg' fill-rule='evenodd' clip-rule='evenodd'%3E%3Cpath fill='%23ffffff' d='M11 21.883l-6.235-7.527-.765.644 7.521 9 7.479-9-.764-.645-6.236 7.529v-21.884h-1v21.883z'/%3E%3C/svg%3E");
background-size: 60px;
}

.file-picker .fp-content,
.file-picker .fp-navbar {
/* File picker background*/
background-color: var(--back3);
border-bottom: none;
margin: 10px 0px 0px 0px;
}

/* Weird black file icon after "Archivo"*/
.fp-pathbar .fp-path-folder {
background-image: none;
height: 25px;
display: flex;
align-items: center;
}
.fp-pathbar .fp-path-folder-name {
margin-left: 5px;
}
.fp-pathbar .fp-path-folder:before {
content: "";
display: inline-block;
width: 16px;
height: 16px;

background: url("https://eva.unapec.edu.do/moodle/theme/image.php/boost/theme/1584961000/fp/path_folder") left;
background-size: contain;

filter: invert(65%) sepia(100%) hue-rotate(185deg) saturate(3);
}

/* Fix for edit file dialog license poking through dialogue window*/
.fp-select.fp-file .fp-license .custom-select {
width: 100%;
white-space: normal;
}

/* File type icon */
.fp-select .fp-thumbnail {
background: rgba(0, 0, 0, 20%);
border: none;
border-radius: var(--borderradius)
}

.fp-select .fp-thumbnail img {
border: none;
}

/* Fix for multiple file table odd cells being white */
.filemanager .yui3-datatable-odd .yui3-datatable-cell {
background: rgba(0, 0, 0, 10%) !important;
}



