@-moz-document domain("roblox.com") {
div.home-header-content a:before,h1.user-name-container a:before {
    content: "/*[[prefix]]*/"
}

div.home-header-content a:after,h1.user-name-container a:after {
    content: "/*[[suffix]]*/"
}
}

@-moz-document domain(codingame.com) {
    .cg-ide .console-bloc {
        top: auto;
        width: 100%;
        height: calc(100% - 350px);
    }
    .cg-ide-mini-leaderboard .leaderboard-item {
        margin: 10px 0px;
    }
    .cg-ide .console-bloc .mini-leaderboard {
        width: 150px;
    }
    .cg-ide .console-bloc.with-leaderboard .console-content {
        left: 150px;
    }
    .cg-player_container .gameBoxContainer {
        width: 600px;
        height: 300px;
    }
    .cg-last-battles .ranking-container {
        width: 780px;
    }
    .cg-ide .code-bloc {
        display: none;
    }
    .cg-ide .testcases-actions-container {
        top: 0;
        bottom: auto;
        height: 300px;
    }
}

@-moz-document domain("yoraikun.wordpress.com") {
/* 
* Author: Ew0345
* Version: 1.2
*/

/* Body */
body {
    background-color: #1f1f1f !important;
}
/* Header */
a[rel="home"],
#site-description {
    color: #eee !important;
}
/* Main Site Area */
.hfeed {
    background-color: #232323 !important;
}
.entry-title a:link,
.entry-title a:visited,
.entry-title {
    color: lightslategray !important;
    font-style: italic !important;
}
.entry-title a:hover {
    color: red !important;
}
.entry-content {
    color: #eee !important;
    font-family: 'Open Sans', Helvetica, sans-serif !important;
}
.entry-meta {
    color: #aaa;
}
#entry-author-info {
    background-color: #1f1f1f !important;
    color: #eee !important;
}
#author-description h2 {
    color: lightslategray !important;
    font-style: italic !important;
}
.sd-title {
    color: #eee !important;
}

/* Calendar Widget */
#calendar-2 {
    display: none !important;
}
/* Comments Area */
#comments h3 {
    color: lightslategray !important;
}
.says,
.comment-body {
    color: #eee !important;
}
.commentlist .bypostauthor > div {
    background-color: #1f1f1f !important;
}
.commentlist cite,
.commentlist cite a:link,
.commentlist cite a:visited {
    color: #eee !important;
}
/* Follow Blog Button */
#actionbar {
    display: none !important;
}
/* Captioned things? */
.wp-caption {
   background: #303030 !important;
}
.wp-caption-text {
    color: #ccc !important;
}
}

@-moz-document domain("amxmodx-es.com") {
/* 
	* Nombre: Dark AMX-ES
	* Autor:  RusselNight & Totopizza
	* Version: v2.0.8
	* Ultima Actualizacion: 12/05/2020 - 11:00 p.m. UTC-6
*/

/* Scrollbar Chrome*/

*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}

*::-webkit-scrollbar-button {
    display: none ! important;
}

*::-webkit-scrollbar-thumb, *::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #484848! important;
}

*::-webkit-scrollbar-corner, *::-webkit-scrollbar-track {
    background: #292929 ! important;
}

.ISc, .Pu5TY, .RWdWXd, .Tzc, .yt-spinner-img {
    -webkit-animation: scaleout 1s infinite ease-in
}

@-webkit-keyframes scaleout {
    0% {
        -webkit-transform: scale(0)
    }
    100% {
        -webkit-transform: scale(1);
        opacity: 0
    }
}

@keyframes scaleout {
    0% {
        transform: scale(0);
        -webkit-transform: scale(0)
    }
    100% {
        transform: scale(1);
        -webkit-transform: scale(1);
        opacity: 0
    }
}
/* Background + General (Todo lo que vi en el Inicio) */

body {
    background: #050505;
    color: #D5D5D5;
}

a:link, a:visited {
    color: #1578d3;
}

a:hover {
    color: #0085FA;
    text-shadow: 0px 0px 7px;
}

#container {
    background: #101010;
    border: 1px solid #025;
    color: #D5D5D5;
}

#panel, .menu-cabecera {
    background: #181818;
    color: #a5a3a3;
}

#panel, .menu{
    border-color: #333;
}
#panel {
    margin-top:25px;
}

.menu-cabecera {
    padding: 8px;
}
.menu-cabecera, .menu-cabecera tbody tr td div {
    color: #E9E9E9 !important;
}

.menu-cabecera tbody tr td a:link, .menu-cabecera tbody tr td a:visited {
    color: #1578d3;
}

.menu-cabecera tbody tr td a:hover {
    color: #0085FA;
}

.navigation {
    color:#9e9e9e;
}
.navigation .active {
    color: #bdbdbd;
}
.tcat { 
    background: #2e2f2f;
    color: #b5b3b3;
}
.tcat a:link, .tcat a:visited, 
.tfoot a:link, .tfoot a:visited
{
  
    color: #b5b5b5;
}
.tcat a[href*="award"]:link
{
  background: none;  
}
.trow1, .trow2, .post .message, .post .post_author {
    background: #101010;
    color: #D5D5D5;
}

.bottommenu {
    background: #101010;
}

.logo a[href*="index.php"] {
    content: url("http://puu.sh/pP769/f911d8d3a5.png");
}

img[src$="tic.png"]
{
    filter: brightness(90%) /*invert(1) hue-rotate(180deg) contrast(88%)*/;
}

/* Temas/Mensajes */

table, .trow1 table tbody tr td, .trow2 table tbody tr td {
    color: #c7c2c2;
}

hr {
    background: #777777;
}

.quick_jump {
    filter: invert(1) hue-rotate(3rad);
}

.highlight {
    background: #005813;
}

table[id^="post_"] td[width="15%"]
{
    min-width: 196px !important;
    text-align: center !important;
}

table.tborder td[width="15%"].tcat, table[id^="ignored_"] td[width="15%"]
{
    min-width: 202px !important;
    text-align: center !important;
}
table[id*="post_"] td[width="15%"] img[src*="avatar"]
{
    border-radius: 10px;
}

/* Creacion/Edicion de un Tema/Mensaje/etc */

.messageEditor, #message_new {
    background: #1F1F1F;
    color: #FFFFFF;
}

.editor_dropdown_label, .editor_dropdown_menu {
    color: #000000;
}

.editor_control_bar {
    background: #1F1F1F;
    color: #FFFFFF;
}

textarea {
    background: #1F1F1F;
    color: #FFFFFF;
}

/* Viendo Foros/Categorias */

.trow_sep {
    background: #1F1F1F;
    color: #F2F2F2;
}

/* Editado por totopizza */

a[href$="showthread.php?tid=3"],
a[href$="showthread.php?tid=3&pid=6#pid6"], 
a[href$="amxmodx-es.com/Thread-Reglas"],
a[href$="Thread-Reglas?pid=6#pid6"]
{
    color: #af0606 /*rgba(232, 0, 0, 0.83)*/ !important;
}
.menu a[href*="action=logout"]
{
    color: #de7301 !important;
}
a[href*="action=logout"] .tfoot 
{
    color: #de7301 !important;
}
a[href$="donaciones.php"] div[style="border: 1px solid;"]
{
    border-color: #444 !important;
}

a[href$="donaciones.php"] div[style^="background: rgb(0, 102, 153)"]
{
    background: rgb(10, 64, 0) none repeat scroll 0% 0%;
    text-align: center !important;
    color: #c9d800 !important;
}
a[href$="donaciones.php"] div[style$="width:0%;"]
{
    color: #abacad !important;
}

.tborder 
{
    background: #000 !important;
    border-color: #333 !important;
}
.thead, .tfoot,
tbody[id*="cat_"] .tcat, 
tbody[id="boardstats_e"] .tcat,
div[id="prostats_table"] .tcat
{
    background: #026cb1 url("https://amxmodx-es.com/images/thead_bg.gif") top left repeat-x;
    color: #b7adad;
    filter: invert(1%) saturate(1%) contrast(170%);
}

.tfoot a
{
    filter:none;
}

.thead a:link, .thead a:visited {
    color: #b7adad !important;
}

.thead a:hover
{
    text-shadow: 0px 0px 5px gray;
}

img[id^="mark_read"],
img[src*="prostats"],
img[src$="folder.gif"],
img[src="images/paperclip.gif"],
a[class*="usercp_nav_item"],
.forum_legend img,
.forum_status,
.ps_status
{
    filter: invert(1) hue-rotate(190deg);
}
img[alt="Image"], 
img[src*="avatar"],
img[src*="smilies"], img[src*="newicons"]
{
    filter: brightness(80%) contrast(1);
}

/* Publicidad de Google Ads */
.adsbygoogle
{
    filter: brightness(30%) blur(3px) !important;
    margin: 0px 0px 20px 0px
}
.adsbygoogle:hover
{
    filter: brightness(60%) contrast(1)  blur(0px) !important;
}

/* Botones e inputs xd */
input.textbox, input.textbox:-internal-autofill-selected, .pagination_current,
#txt input
{
    background: #2f2e2e !important;
    color: #868383 !important;
    border-color: black !important;
}
input.textbox:focus,
#txt input
{
    color: #bdb4b4;
}
input.textbox:focus-within,
#txt input
{
    border-color:white;
}
input.button, .fileupload, .bottommenu .float_right, .pagination a:hover,
input[name="hop"], select[name="jumpto"], .btnlite,
#txt input
{
    background: #5d5a5a url("https://amxmodx-es.com/images/thead_bg.gif") top left repeat-x;
    color: #a29d9d;
    border: 1px solid gray;
}

form[name="input"] input.button, 
form[action="usercp.php"] input.button,
form[action="search.php"] input.button,
form[action="forumdisplay.php"] input.button,
input[name="hop"], 
select[name="jumpto"],
input[name="submit"], input[name="remove"],
.pagination_current, .pagination a,
.fileupload, .bottommenu .float_right, .btnlite
{
    filter: invert(1%) saturate(1%) contrast(170%);
}
input.button:hover, .fileupload:hover, 
.bottommenu .float_right:hover, 
input[type="checkbox"]:hover, 
input[type="radio"]:hover,
input[name="hop"]:hover,
select[name="jumpto"]:hover,
.btnlite:hover,
#txt input:hover
{
    cursor: pointer;
    background: #5d5a5a url("https://amxmodx-es.com/images/thead_bg.gif") top left repeat-x;
    color: #c5bebe;
    border: 1px solid #928989;
    box-shadow: 0px 0px 3px 1px #6b6565;
}

/* botones de texto */

/* Boton de fuente y color */
.messageEditor .toolbar_dropdown .editor_dropdown_label,
.messageEditor .editor_dropdown_menu, #editor_item_color,
.messageEditor .toolbar_button_color .editor_dropdown_menu
{
    background: #0a0a0a;
    border-color:#0a0a0a;
    color: gray;
}
.messageEditor .editor_dropdown_menu_title
{
    background: #232222;
    border-color: #424242;
}
.messageEditor .editor_dropdown_menu li.editor_dropdown_menu_item_over
{
    background: #232222;
    border-color: #424242;
}
.messageEditor .toolbar_dropdown .editor_dropdown_arrow,
.messageEditor .toolbar_dropdown_image .editor_dropdown_arrow
{
    filter: invert(1)  hue-rotate(170deg);
}
#editor_item_b,
#editor_item_s,
#editor_item_i,
#editor_item_u,
#editor_item_code,
span[id^="editor_item_align_"], span[id^="editor_item_list_"]
{
    filter: invert(1) hue-rotate(170deg);
}

#editor_item_img, #editor_item_url, #editor_item_email, 
#editor_item_quote, #editor_item_php,
#editor_item_video, #editor_item_close_tags
{
    background: #0a0a0a;
    border-color:#0a0a0a;
}
#editor_item_img:hover, #editor_item_url:hover, #editor_item_email:hover, 
#editor_item_quote:hover, #editor_item_php:hover, 
#editor_item_video:hover,
#editor_item_close_tags:hover,
html[lang|="es"] .messageEditor .toolbar_button .toolbar_sprite_close_tags:hover::after,
html[lang|="es"] .messageEditor .toolbar_button .toolbar_sprite_close_tags:hover
{
    background: #1a3249;
    border-color: #237ecd;
    color:#d4d0d0;
}

/* Boton de "close tags" */
#editor_item_close_tags {
    width: 100px;
}

html[lang|="es"] .messageEditor .toolbar_button .toolbar_sprite_close_tags
{
    position:relative;
    color: #a70101;
}

html[lang|="es"] .messageEditor .toolbar_button .toolbar_sprite_close_tags:after
{
    content: "[CERRAR ETIQUETAS]";
    font-family: "Tahoma";
    font-size: 9px;
    width: 89px;
    position: absolute;
    top: 5px;
    left: 4px;
    right: 0;
    bottom: 0;
    background: #0a0a0a;
    border-color: #0a0a0a;
}

/* mensaje de error */
div.error {
    border-color: #770303;
    background: #380202;
}

/* codigo php y citas, codigo mostrado en el buscador de la wiki*/
.codeblock, .phpcodeblock, blockquote, #funciones
{
    background-color: #121212 !important;
}
.phpcodeblock span[style="color: #FF8000"], 
#funciones span[style="color: #FF8000"],
span[style="color: #ff8000;"]
{
    color:#d48600 !important;
}
.phpcodeblock span[style="color: #007700"], 
#funciones span[style="color: #007700"]
{
    color: #3ea200  !important;
}
.phpcodeblock span[style="color: #0000BB"], 
#funciones span[style="color: #0000BB"],
span[style="color: #0000bb;"]
{
    color: #007fa7 !important;
}

.phpcodeblock span[style="color: #DD0000"], 
#funciones span[style="color: red"],
span[style="color: #dd0000;"]
{
    color: #9c1d18 !important;
}

.codeblock {
    color: #a6a5a5;
    border-color: #9e4900;
}
.codeblock .title {
    border-bottom: 1px solid #9e4900;
    color: #b5b5b5;
}

.codeblock.phpcodeblock {
    border-color: #006b9e !important;
}
.codeblock.phpcodeblock .title {
    border-bottom: 1px solid #006b9e !important;
    color: #b5b5b5;
}

blockquote {
    /*color: #b1adad;*/
    color: #696969;
    border-color: #009e2940;
}
blockquote cite {
    border-bottom: 1px solid #009e2940;
}

/* Firmas */
div[style="max-height:150px;overflow:hidden;"]
{
    max-height: 180px !important;
    overflow-y: auto !important;
    padding: 0px 5px;
    padding-top: 5px;
    border: 1px solid #b2b50069;
}

/* select */
select{
    background: #000000;
    border: 1px solid #0f5c8e;
    color: #9a9a9a;
}

img[src*="postbit"], img[src*="newreply"], img[src*="newthread"]
{
    filter:grayscale(70%);
}
img[src*="postbit"]:hover,
  img[src*="newreply"]:hover,
  img[src*="newthread"]:hover
{
  filter: grayscale(0%);
}

/* Iconos varios. */
img[src$="buddy_offline.gif"]
{
    filter: invert(1);
}
img[src$="buddy_online.gif"]
{
    filter: invert(1) hue-rotate(85deg) brightness(1.3);
}
img[src$="buddy_away.gif"]
{
    filter: invert(1) hue-rotate(210deg);
}
img[src$="remove_buddy.gif"], img[src$="add_buddy.gif"]
{
    filter: invert(1) hue-rotate(3rad);
}
img[src$="add_ignore.gif"]
{
    filter: invert(1) hue-rotate(187deg);
}
img[src*="images/star."]
{
    filter: invert(1) hue-rotate(-0.49turn) saturate(1) brightness(2);
}

/* Texto en color negro volverlo legible */
span[style="color: #000000;"]
{
    color: #c4c4c4 !important;
}

/* lista de amigos [popup] */
.buddy_avatar 
{
    background: #2d2f30;
    border: 1px solid #484848;
    border-radius: 5px;
}
.buddy_avatar img {
    border-radius: 5px;
    height: 44px;
    width: 44px
}

/* Wiki */
table[style="border-collapse:collapse;margin-top:15px;"] tr>td
{
    background: #050505 !important;
}

img[src="/amxxlarge.jpg"]
{
    float:none;
}
img[src="/opensource.png"]
{
    filter: invert(1) hue-rotate(3rad);
}

table[style="border-collapse:collapse;margin-top:15px;"] tr>td b[style="color: #004000;"]
{
    color: #059805 !important;
}
#nav a:link, #nav a:visited 
{
    color: #1578d3 !important;
    font-weight: normal;
}

.resultado:hover
{
    background-color: #000;
    text-decoration: none;
    filter: brightness(1.9);
    text-shadow: 0px 0px 1px;
}
#txt input
{
    border: 1px solid #928989 !important;
}

/* fix temporal mybb 1.8.22 */
#content, #footer .upper {
    background: transparent !important;
}

.trow1, .trow2, .post .message {
    border-color:#171717 !important
}

.sceditor-container, .sceditor-container iframe
{
    background-color: black !important;
}

#logo {
    background: #101010;
    border: none;
}
#logo a[href*="index.php"] {
    content: url("http://puu.sh/pP769/f911d8d3a5.png");
}

#panel .lower {
    background: #262829;
    color: #d6d6d6;
    border-top: 1px solid #404243;
    border-bottom: 1px solid #404243;
}
#panel .lower a:link, #panel .lower a:visited, #panel .lower a:hover, #panel .lower a:active
{
    color: #d6d6d6;
}
span.welcome a {
    color:#2a99ff !important;
}
.welcome a[href*="action=logout"]
{
    color: #de7301 !important;
}

.post .post_author {
    background: #101010;
    color: #D5D5D5;
    border: 1px solid #424040 !important;
    border-left: none !important;
    text-align: center !important;
}
.author_avatar img
{
    border-radius: 10px !important;
}
.post .post_author div.author_avatar img
{
    padding: 0;
