}

.post_controls {
    background: #262829;
}
.postbit_buttons > a:link, .postbit_buttons > a:hover, .postbit_buttons > a:visited, .postbit_buttons > a:active
{
    background: #b1b1b1;
    border: 1px solid #000;
    color:black;
    
}
.postbit_buttons > a:hover
{
    text-shadow: none;
