    padding-top: 5px;
    border: 1px solid #b2b50069;
}

.codeblock code
{
    max-height:650px;
}

.popup_menu {
    z-index: 999999;
}

/* Iconos faltantes */

img[alt=".ini"], img[alt=".rar"], img[alt=".sql"], img[alt=".sma"], img[alt=".inc"]
{
    box-sizing:border-box;
    display: inline-block;
    width:16px;        /*Ancho de la imagen nueva, aunque en este caso son iguales.*/
