    padding-left:16px; /*Padding igual al ancho de la imagen nueva*/
    background-size: 16px;
    overflow:hidden;
}
img[alt=".ini"]
{
    background-image: url("https://amxmodx-es.com/attachment.php?aid=7059");
}
img[alt=".rar"]
{
    background-image: url("https://amxmodx-es.com/attachment.php?aid=7060");
}
img[alt=".sql"]
{
    background-image: url("https://amxmodx-es.com/attachment.php?aid=7061");
}
img[alt=".sma"]
{
    background-image: url("https://amxmodx-es.com/attachment.php?aid=7063");
}
img[alt=".inc"]
{
    background-image: url("https://amxmodx-es.com/attachment.php?aid=7064");
}

/* mybb bar code */

.sceditor-container iframe, .sceditor-container textarea
{
    background: black;
    color: #ddd;
}
.sceditor-container
{
    border: 1px solid #6f6969;
}
div.sceditor-toolbar
{
    background: #808080;
}
div.sceditor-group {
    background:#929090;
    border-bottom: 1px solid #565656;
}
.sceditor-button
{
    border: 1px solid #777;
}
.sceditor-button:hover, .sceditor-button:active, .sceditor-button.active
{
    background: #cecece;
    border: 1px solid #6f6d6d;
}
div.sceditor-dropdown
{
    border: 1px solid #adadad;
    background: padding-box #808080;
    border-radius: 15px;
}

/* Color del texto del post */
.post_body {
    color: #847f7f;
}
/* Color en algunos nombres de usuarios */
a[href$="amxmodx-es.com/Neeeeeeeeeel"]
{
    color: #d20000;
    text-shadow: 0px 0px 7px;
    background: url(https://i.imgur.com/aATZooy.gif);
}
a s 
{
    color: gray;
}

/* color para el tema de desvirtue */
a[title="Desvirtue aquí"], a[href*="Thread-Desvirtue-aqu%C3%AD"]
{
    color: #ec9005 !important;
    text-shadow: 0px 0px 7px #ff5200;
}
font[style="line-height:10px;"]
{
    color: #1578d3;
    text-shadow: none;
}

/* Paginas */
.popup_menu.drop_go_page
{
    background-color: #808080;
    border-color: #585858;
}

.pagination a {
    background-color: gray;
    color: #d0d0d0 !important;
    border-color: gray;
}

/* color reputacion en el last posts */
td[align="left"] a[href*="reputation.php"]
{
    color: green;
