data.css:
/*
Author: FlameCaro
Navigationbar: Samarti
*/

@-moz-document url-prefix("https://www.horsereality.com"), url-prefix("https://www.horsereality.nl") {
  
  /*Fonts*/
  @font-face {
    font-family: 'Lobster Two';
    font-style: normal;
    font-weight: 400;
    src: local('Lobster Two'), local('LobsterTwo'), url(https://fonts.gstatic.com/s/lobstertwo/v11/BngMUXZGTXPUvIoyV6yN5-fN5qU.woff2) format('woff2');
  }
  @font-face {
    font-family: 'Quicksand';
    font-style: normal;
    font-weight: 400;
    src: local('Quicksand Regular'), local('Quicksand-Regular'), url(https://fonts.gstatic.com/s/quicksand/v9/6xKtdSZaM9iE8KbpRA_hK1QN.woff2) format('woff2');
  }
  @font-face { /*latin*/
    font-family: 'Parisienne';
    font-style: normal;
    font-weight: 400;
    src: local('Parisienne'), local('Parisienne-Regular'), url(https://fonts.gstatic.com/s/parisienne/v6/E21i_d3kivvAkxhLEVZpQyhwDw.woff2) format('woff2');
  }
  @font-face {
    font-family: 'Miniver';
    font-style: normal;
    font-weight: 400;
    src: local('Miniver'), url(https://fonts.gstatic.com/s/miniver/v8/eLGcP-PxIg-5H0vC37oIyg.woff2) format('woff2');
  }
  @font-face {
    font-family: 'Fredericka the Great';
    font-style: normal;
    font-weight: 400;
    src: local('Fredericka the Great'), local('FrederickatheGreat'), url(https://fonts.gstatic.com/s/frederickathegreat/v9/9Bt33CxNwt7aOctW2xjbCstzwVKsIBVV--Sjxbc.woff2) format('woff2');
  }
  
  /*Update/Change page*/
  .container_12 .center-area {
    background-color: #896554;
  }
  
  h3.socials a:active, h3.socials a:link, h3.socials a:visited {
    color: #CFB082;
  }

  
   /*Top bar*/
  .bluebar_container {
    background-color: #5D3637;
  }
  ul li a, ul li a:active, ul li a:link, ul li a:visited {
    color: #CFB082;
  }
  .gamestats a:visited{
    color: #CFB082;
  }
  .gamestats{
    color: #CFB082;
  }
  
  /*User menu*/
  #bbmenu.user ul.slidedown li ul.slidedownmenu { 
    background-color: #5D3637;
  }
  #bbmenu.user ul.slidedown li a.arrow {
    background-image: url(https://i.imgur.com/JREmr58.png);
    font-family: Quicksand;
  }
  .slidedownmenu li a.logout {
    background-image: url(https://i.imgur.com/NTzeO72.png);
  }
  #bbmenu.user ul.slidedown li:hover, #bbmenu.user ul.slidedown li:focus, #bbmenu.user ul.slidedown li:active {
    background-color: transparent;
  }
  #bbmenu.user ul.slidedown li a:hover, .toplinks ul li a:hover, .gamestats a:hover {
    color: #C38343;
  }
  .statusbar {
    background-color: #CFB082;
    border: 2px solid #896554;
  }
  #bbmenu.user ul.slidedown li ul.slidedownmenu li .ava {
    border: 1px solid #CFB082;
  }
  #bbmenu.user ul.slidedown li ul.slidedownmenu li .level p {
    color: #CFB082;
  }
  .xpvalue {
    background: -moz-linear-gradient(top,#E1AE52 0%,#C38343 100%);
  }
  
  /*Navigation*/
  .leftnav { 
    background: -webkit-linear-gradient(top,#5D3637 0%,#896554 100%);
    border-bottom-width: 2px;
    border-right-width: 2px;
    border-left-width: 2px;
    border-radius: 0 0 85px 85px;
    border-top-color: #896554;
    border-right-color: #896554;
    border-bottom-color: #896554;
    border-left-color: #896554;
    border-top-style: ridge;
    border-right-style: ridge;
    border-bottom-style: ridge;
    border-left-style: ridge;
    box-shadow: none;
    margin-left: -2px;
    width: 148px;
    height: 650px;
    margin-top: -1px;
  }
  .leftnav > ul > li > ul > li a:active, .leftnav > ul > li > ul > li a:link, .leftnav > ul > li > ul > li a:visited {
    color: #CFB082;
  }
  .leftnav > ul > li, .leftnav > ul > li a:active, .leftnav > ul > li a:visited {
    color: #CFB082;
  }
  .subtitle {
    font-family: Fredericka the great;
    color: #DC8459;
  }
  
  /*DP Banner*/
  .header-cta {
    background-color: #896554;
  }
  .header-cta h2 {
    color: #fff;
  }

  
  /*Body*/
  .center { 
    background-color: #CFB082;
  }
  body.background {
    background: #5D3637 url("https://i.imgur.com/Cr3Kn2J.jpg") no-repeat fixed 0 0/100% auto !important;
  }
  .container {
    background-color: #CFB082;
  }
  body.background {
    background: #5D3637 url("https://www.horsereality.com/images/body-background-blur.jpg") no-repeat fixed 0 0/100% auto;
  }
  
  /*Premium*/
  .premium-only {
    border: 1px #AF223B solid;
    background: #AF223B42;
  }
  h3 {
    color: #CFB082;
  }
  
  /*Lists*/
  .even { 
    background-color: #896554
  }
  .odd {
    background-color: #5D3637;
  }
  .subtop {
    color: #CFB082;
  }
  
  /*Pages*/
  .numbers {
    background: #896554;
  }
  .numselected {
    color: #CFB082;
    background: #5D3637;
  }
  
  /*Selection bars*/
  input, select { 
    background-color: #896554;
    border-color: #5D3637;
  }
  .store_flex select {
    background: url(https://www.horsereality.com/images/bottom-arrow-blue.png) 100% 50% no-repeat #896554;
    border: 1px solid #5D3637;
  }
  
  /*Training bars*/
  .trainbar, .energybar_half, .energybar, .horse_statusbar, .training_right > .block > .barcon > .trainbar {
    background-color: #896554;
    border: 2px solid #896554;
  }
  .trainbar > .exp, .energy, .horse_xp, .training_right > .block > .barcon > .trainbar > .trained {
    background: -moz-linear-gradient(top,#E1AE52 0%,#C38343 100%);
  }
  
  /*Bottom bar*/
  .footer { 
    background-color: #5D3637;
  }
  .footer h6 {
    color: #CFB082;
  }
  .footer ul li a:visited, .footer ul li a:link {
    color: #CFB082;
  }
  
  /*Errors*/
  .alert.error { 
    background-color: #AF332B;
    border: 1px solid #5D3637;
    color: #701E22;
  }
  
  /*Links*/
  .col_750 a:active, .col_750 a:link, .col_750 a:visited , .col_700 a:active, .col_700 a:link, .col_700 a:visited, #mail_right p a:hover, table tr td a:active, table tr td a:link, table tr td a:visited, p a:active, p a:link, p a:visited, .col_200 a:active, .col_200 a:link, .col_200 a:visited, .col_150 a:active, .col_150 a:link, .col_150 a:visited, .col_500 a:active, .col_500 a:link, .col_500 a:visited, .adopt_blocktitle a:active, .adopt_blocktitle a:link, .adopt_blocktitle a:visited, .adopt_blocktext p a:active, .adopt_blocktext p a:link, .adopt_blocktext p a:visited, .store_title a:active, .store_title a:link, .store_title a:visited, .col_100 a:active, .col_100 a:link, .col_100 a:visited, .col_300 a:active, .col_300 a:link, .col_300 a:visited, .column_200 a:active, .column_200 a:link, .column_200 a:visited, .horse_left .right a:active, .horse_left .right a:link, .horse_left .right a:visited {
    color: #CFB082;
  }
  .col_750 a:hover, .miniparents a:hover p, .minigparents a:hover p, .miniggparents a:hover p, .col_700 a:hover, .ggrandparents a:hover p, .grandparents a:hover p, .parents a:hover p, table tr td a:hover, p a:hover, .col_200 a:hover, .col_150 a:hover, .col_500 a:hover, .col_100 a:hover, .col_300 a:hover, .column_200 a:hover, .horse_left .right a:hover {
     color: #AF332B;
  } 
   #mail_right p a:active, #mail_right p a:visited, #mail_right p a:link, .adopt_blocktitle a:hover, .adopt_blocktext p a:hover, .horse_left .right a:hover  {
     color: #5D3637;
  }
  
  /*Advertising banners*/
  span.shoutout.bestdeal {
    background-color: #E1AE52;
  }
  span.shoutout.popular {
    background-color: #AF332B;
    color: #CFB082;
  }
  
  /*NPC boxes*/
  .npc_box { 
    margin: 7px;
    background-color: #896554;
    border: 3px solid #896554;
  }
  
  /*Container and their borders*/
  .top { 
    background-color: #896554;
    color: #CFB082;
  }
  .container_12 .grid_8.training_left {
    background-color: #5D3637;
    border-left: 3px solid #896554;
    border-bottom: 5px solid #896554;
    border-right: 3px solid #896554;
    margin-left: 7px;
  }
  .training_left .horsetraining .traincon {
    background-color: #701E22;
  }
  
  /*Buttons*/
  button.yellow, input[type="submit"].yellow { 
    background-color: #D45E30;
  }
  button.dark, input[type="submit"].dark, button.orange, button.darkblue, input[type="submit"].darkblue {
    background-color: #5D3637;
    color: #CFB082;
  }
  button.red, input[type="submit"].red {
    background-color: #AF332B;
  }
  
  /*Buy DP*/
  .container_12 .grid_6.half_block, .grid_12.deltapoints {
    background-color: #896554;
    border-left: 3px solid #896554;
    border-bottom: 5px solid #896554;
    border-right: 3px solid #896554;
    margin: 7px;
  }
  .buy_block .buy_top {
    background-color: #5D3637;
  }
  .buy_block .buy_top h3 {
    color: #CFB082;
  }
  .grid_12.deltapoints .buy_container .buy_block {
    background-color: #701E22;
  }
  .buy_explain h5, html body#deltapoints.background.europe div.container div.container_12.center div.text div.table_con div.grid_12.deltapoints div.row_960 p strong {
    color: #D45E30 !important;
  }
  
  /*Veteranian*/
  .vet_blocks { 
    background-color: #896554;
  }
  
  /*Equistore*/
  .storeblocks, .horseblocks { 
    background-color: #896554;
  }
  #store_left.tabnav .store_tabclick:hover, #store_left.tabnav .store_tabclick.tabsel {
    background-color: #B89764;
  }
  #store_left {
    width: 140px;
  }
  .breadcrumbs, .breadcrumbs a:link, .breadcrumbs a:visited, .breadcrumbs a:active {
    background-color: #896554;
    color: #CFB082;
  }
  
  /*Laboratory*/
  .genetic_table_row {
    border-bottom: 1px solid #896554;
  }
  .genetic_result, .genetic_tests .test_block .first, .grid_6.genetics {
    background-color: #5D3637;
  }
  .grid_6.genetics {
    border-left: 1px solid #896554;
    border-bottom: 5px solid #896554;
    border-right: 1px solid #896554;
  }
  .subtop {
    background-color: #701E22;
  }
  .grid_6.genetics .subtop {
    font-weight: 1000;
  }
  .lab_selectall {
    background-color: #896554;
    color: #CFB082;
  }
  
  /*University*/
  .adopt_blocks { 
    background-color: #896554;
  }
  .uni_blocks {
    background-color: #896554;
  }
  .container_12 .grid_12.stable_block {
    border-left: 3px solid #896554;
    border-bottom: 5px solid #896554;
    border-right: 3px solid #896554;
    margin: 7px;
  }
  .uni_blockimg {
    background-color: #5D3637;
  }
  .mavatar {
    border: 0px solid #fff;
  }
  .uni_statusbar {
    background-color: #896554;
    border: 2px solid #5D3637;
  }
  .uni_xp {
    background: -moz-linear-gradient(top,#E1AE52 0%,#C38343 100%);
  }
  .alert.success {
    background-color: #E1AE52;
    border: 1px solid #5D3637;
    color: #5D3637;
  }
  h4 {
    color: #5D3637;
  }
  
  /*Market*/
  form fieldset label, fieldset label {
    color: #000;
  }
  
  /*Profile*/
  .profile_menu {
    background-color: #5D3637;
    border-top: 2px solid #896554;
    border-bottom: 2px solid #896554;
  }
  .avatar {
    border: 0px solid #896554;
    margin-top: 0px !important;
  }
  .profile_username h1,  .profile_ranking, .profile_menu ul li, .profile_account h2 {
    color: #CFB082;
  }
  .profile_username h1 {
    font-family: Parisienne;
  }
  .profile_left {
    margin-top: 110px;
  }
  .profile_account {
    background-color: #896554;
    border: 1px solid #5D3637;
    border-radius: 10px;
  }
  .tabdetails {
    background-color: #896554;
    border: 1px solid #5D3637;
    border-radius: 20px;
  }
  html body#user.background.europe div.container div.container_12.center div.profile_left div.profile_account h2 i.fa.fa-user {
    margin-left: 77px;
  }
  .fa-home::before {
    margin-left: 86.5px;
  }
  .fa-graduation-cap::before {
    margin-left: 88.25px;
  }
  .milestone_text h2 {
    color: #CFB082;
  }
  .profile_username h2 {
    color: #CFB082;
    text-shadow: none;
    margin-top: 35px;
    margin-left: 380px;
    font-size: 14px;
    font-weight: 500;
    width: 300px;
  }
  .profile_username {
    margin-top: 240px;
    margin-left: 200px;
    height: 50px;
  }
  .profile_ranking {
    margin-right: 0px;
  }
  .profile_menu ul li:hover, .profile_menu ul li.tabclick:hover {
    background: none !important;
    color: #896554 !important;
  }
  .profile_menu ul li {
    font-family: Miniver;
  }
  
  /*Profile - Horses*/
  table thead tr.top {
    display: none !important;
  }
  table thead tr.sub {
    background-color: #4a7a9b00;
    color: #CFB082;
  }
  table thead {
    border-bottom: 1px solid #5D3637;
  }
  table tr.odd {
    background-color: #eaf0f200;
  }
  table tr.even {
    background-color: #5D36377D;
  }
  table {
    background-color: #081b2800;
    border-bottom: 0px solid #081b28;
    border-right: 0px solid #081b28;
    border-left: 0px solid #081b28;
  }
  
  /*Messages*/
  .mail_row {
    background-color: #896554;
    border-bottom: 1px solid #5D3637;
  }
  .mail_row:hover {
    background-color: #5D363780;
  }
  #mail_right p a:active, #mail_right p a:visited, #mail_right p a:link {
    color: #CFB082;
  }
  #mail_right p a:hover {
    color: #5D3637;
  }
  .mail_text {
    border-bottom: 3px solid #896554;
    border-right: 3px solid #896554;
    border-left: 3px solid #896554;
    background-color: #E2CDAE;
  }
  .mail_subheader, .mail_header {
    background-color: #896554;
    padding: 5px 9px 5px 10px;
  }
  
  /*Edit Profile*/
  html body#user.background.europe div.container div.container_12.center div.grid_12.stable_block form div.row_960, html body#user.background.europe div.container div.container_12.center div.grid_12.stable_block form div.row_960 {
    background-color: #896554 !important;
  }
  .grid_12 form fieldset label, .grid_12 fieldset label {
    color: #000;
  }
  
  /*Horses*/
  .block_tabclick:hover, .block_tabclick.tabsel {
    background-color: #B89764;
  }
  
  /*Foal Pastures*/
  .adopt_blockimg.white {
    background-image: url(https://i.imgur.com/Cr3Kn2J.jpg) !important;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center center;
  }
  .adopt_blockimg.white.locked {
    background-image: url(https://i.imgur.com/qYt1MkE.jpg) !important;
  }
  .store_flex select {
    background: url(https://i.imgur.com/JREmr58.png) 100% 50% no-repeat #896554;
  }
  .adopt_blockimg {
    background: url(https://i.imgur.com/yC2zC3k.jpg) no-repeat !important;
    background-size: cover !important;
  }
  
  /*Currency exchange*/
  .exchange_actions_window, .exchange_books_window, .exchange_orders_window {
    background-color: #896554;
    border: 1px #5D3637 solid;
  }
  table tr, .exchange table thead th {
    background-color: #5D3637;
  }
  .exchange_actions_window table td div input {
    border: 1px #000 solid;
  }
  hr {
    border-top: 1px solid #000;
  }
  .exchange_actions_window_buy h2 {
    border-bottom-color: #02b002;
  }
  .exchange_actions_window_sell h2 {
    border-bottom-color: #b00000;
  }
  .exchange_actions_window table td div span {
    background-color: #CFB082;
  }
  .exchange table {
    border: 0px #000 solid;
  }
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_books div.exchange_books_window.exchange_books_window_buy div.tablescroll div.tablescroll_wrapper, html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_books div.exchange_books_window.exchange_books_window_buy div.tablescroll table.tablescroll_head,
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_books div.exchange_books_window.exchange_books_window_sell div.tablescroll,
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_orders div.exchange_orders_window.exchange_orders_window_history div.tablescroll {
    margin-left: -7px;
  }
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_books div.exchange_books_window.exchange_books_window_buy div.tablescroll table.tablescroll_head,
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_books div.exchange_books_window.exchange_books_window_buy div.tablescroll div.tablescroll_wrapper,
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_books div.exchange_books_window.exchange_books_window_sell div.tablescroll table.tablescroll_head,
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_books div.exchange_books_window.exchange_books_window_sell div.tablescroll div.tablescroll_wrapper,
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_orders div.exchange_orders_window.exchange_orders_window_history div.tablescroll table.tablescroll_head,
  html body#currency-exchange.background.europe div.container div.container_12.center div.text div.exchange div.exchange_orders div.exchange_orders_window.exchange_orders_window_history div.tablescroll div.tablescroll_wrapper {
    width: 450px !important;
  }
  .exchange_books_window table tbody tr {
    background-color: #896554;
  }
  .exchange_books_window table tbody tr:nth-child(2n) {
    background-color: #5D3637;
  }
  .exchange table td {
    border: 1px #000 solid;
  }
  .exchange_orders_window_history .sell td:nth-child(2) {
    color: #b00000;
  }
  
  /*Forum*/
  #forum .row_960.no_padding {
    background-color: #896554;
  }
  #forum .row_960 .col_750.mod {
    background: url(https://i.imgur.com/kw7tW12.png) no-repeat right top #896554;
  }
  
  /*Horse page*/
  .horse_left, .looking_at {
    background-color: #896554c7;
    border-radius: 10px;
  }
  .looking_at p {
    color: #CFB082;
  }
  .horse_left h1 {
    font-size: 28px;
    color: #CFB082;
    font-family: Lobster Two;
  }
  /*Tabs*/ .tabclick, .tabclick2, .tabclick3 { 
    background-image: url("https://i.imgur.com/Cr3Kn2J.jpg") !important;
    background-size: 100% !important;
    background-position-y: 0px !important;
    color: #FFF0DC;
  }
  .tabclick.tabsel, .tabclick2.tabsel, .tabclick3.tabsel {
    background-image: url("https://i.imgur.com/yC2zC3k.jpg") !important;
    background-size: 100% !important;
    background-position-y: 0px !important;
    color: #FFFDFB;
  }
  /*Training*/ .container_12 .grid_4.training_right { 
    background-color: #5D3637;
    border-left: 3px solid #896554;
    border-bottom: 5px solid #896554;
    border-right: 3px solid #896554;
    margin-left: 7px;
  }
  .training_right > .block, .training_right > .block > .comp img, .training_right > .block > .show img, .horse_blockimg {
    background-color: #5D3637;
  }
  .training_left h6 {
    color: #896554;
    background-color: #492728;
  }
  .traincon.selected {
    background-color: #88241E !important;
  }
  html body#horses.background.europe div.container div.container_12.center div.tabdetails div#tab_training2.tabtext div div.table_con div.grid_4.training_right {
    margin-left: 4px;
  }
  /*Summary*/ .pedigree {
    background-color: #5D3637;
  }
  /*Genetics*/ .genetic_potential {
    background-color: #5D3637;
  }
  .curly-quotes::before, .curly-quotes::after {
        color: #CFB082;
  }
  /*Achievments*/ .row_300 { 
    background-color: #5D3637;
  }
  html body#horses.background.europe div.container div.container_12.center div.tabdetails div#tab_achievements2.tabtext div.table_con div.grid_8.training_left.conformation {
    margin-left: 4px;
  }
  /*Update*/ html body#horses.background.europe div.container div.container_12.center div.tabdetails div#tab_update2.tabtext div.table_con div.grid_6.half_block div.row_960.half.even
  {
    background-color: #5D3637;
