    display: inline-block;
    border-radius: 2px;
    padding: 0 5px;
    font-size: 90%;
    color: #fff;
    text-decoration: none!important;
    background: #828282;
}
.cMultiQuote {
    border: 3px solid #484848!important;
}
.ipsTags .ipsTag, .ipsTag_prefix {
    background: #474747;
    color: #fff!important;
    font-size: 10px;
    line-height: 18px;
    height: 18px;
    padding: 0 7px;
