    display: block;
    position: relative;
    letter-spacing: 0;
    border-radius: 0 4px 4px 0;
}
.ipsItemStatus:not( .ipsItemStatus_large ) {
    color: #a05664;
    font-size: 14px;
    line-height: inherit;
    vertical-align: middle;
}
.right-ups .fa {
    background: #5e5e5e;
    color: #e6e8ea;
    height: 20px;
    font-size: 10px;
    line-height: 20px;
    text-align: center;
    width: 20px;
    margin-right: -4px;
    margin-left: 4px;
    border-radius: 2px;
}
