@-moz-document domain("news.ycombinator.com") {
:root {
    --background-color: #f6f6ef;
    --accent-color: #f60;
    --text-color: #000;
    --visited-link-color: var(--grey-82, #828282);
    --pagetop-color: #222;
    --topsel-color: #fff;
    --grey-00: #000;
    --grey-5a: #5a5a5a;
    --grey-73: #737373;
    --grey-82: #828282;
    --grey-88: #888;
    --grey-9c: #9c9c9c;
    --grey-ae: #aeaeae;
    --grey-be: #bebebe;
    --grey-ce: #cecece;
    --grey-dd: #ddd;
}
body {
    margin: 0;
}
body, td, .default, .admin, .admin td, .title, .subtext, .subtext td, .yclinks, .pagetop, .comhead, .comment {
    font-family: Verdana;
}
body, td, a:visited, .title, .subtext, .subtext td, .subtext a:link, .subtext a:visited, .yclinks, .comhead, .comhead a:link, .hnmore a:link {
    color: var(--visited-link-color);
}
a:link, .admin, .admin td {
    color: var(--text-color);
}
.pagetop, .pagetop a, .hnname {
    color: var(--pagetop-color);
}

.topsel a:link, .topsel a:visited {
    color: var(--topsel-color);
}
.c00, .c00 a:link {
    color: var(--grey-00);
}
.c5a, .c5a a:link, .c5a a:visited {
    color: var(--grey-5a);
}
.c73, .c73 a:link, .c73 a:visited {
    color: var(--grey-73);
}
.c82, .c82 a:link, .c82 a:visited {
    color: var(--grey-82);
}
.c88, .c88 a:link, .c88 a:visited {
    color: var(--grey-88);
}
.c9c, .c9c a:link, .c9c a:visited {
    color: var(--grey-9c);
}
.cae, .cae a:link, .cae a:visited {
    color: var(--grey-ae);
}
.cbe, .cbe a:link, .cbe a:visited {
    color: var(--grey-be);
}
.cce, .cce a:link, .cce a:visited {
    color: var(--grey-ce);
}
.cdd, .cdd a:link, .cdd a:visited {
    color: var(--grey-dd);
}
table {
    border-spacing: 0;
    border-collapse: collapse;
}
/* container */
 table#hnmain {
    background-color: var(--background-color);
    width: 100%;
    height: 100vh;
}
/* header */
 table#hnmain > tbody > tr:first-child > td {
    background: var(--accent-color);
    height: 48px;
    padding: 0 8px;
    position: sticky;
    top: 0;
    z-index: 1;
}
table#hnmain > tbody > tr:first-child > td > table {
    padding: 0 !important;
}
/* content */
 table#hnmain > tbody > tr:nth-child(3) > td {
}
.itemlist {
    margin: 0 16px;
}
span.rank {
    display: none;
}
tr#pagespace {
    height: 16px !important;
}
.spacer {
    height: 8px !important;
}
/* footer */
 table#hnmain > tbody > tr:last-child > td {
    position: sticky;
    bottom: 0;
    background-color: var(--background-color);
}
table#hnmain > tbody > tr:last-child > td > img {
    display: none;
}
table#hnmain > tbody > tr:last-child > td > table td {
    padding: 1px 0 0 0 !important;
}
@media(prefers-color-scheme: dark) {
    :root {
        --background-color: #090910;
        --accent-color: #f60;
        --text-color: #fff;
        --visited-link-color: var(--grey-82, #828282);
        --pagetop-color: #000;
        --topsel-color: #000;
        --grey-00: #fff;
        --grey-5a: #a5a5a5;
        --grey-73: #8c8c8c;
        --grey-82: #7d7d7d;
        --grey-88: #777;
        --grey-9c: #636363;
        --grey-ae: #515151;
        --grey-be: #414141;
        --grey-ce: #313131;
        --grey-dd: #222;
    }
}
}

