/* The unlock Hibernal Den space page */
.common-message,
.den-slot-unlock {
    margin-top: 1px !important;
    margin-bottom: 1px !important
}
.den-slot-unlock {
    background: #8c8c8c80 !important
}
.den-slot-unlock-name {
    line-height: inherit !important;
    margin-bottom: 0px !important;
    background: #dcd6c8 !important;
    padding: .1em .5em .1em .5em !important;
    border: none !important
}
.den-slot-unlock-reward {
    margin-top: 0px !important;
    margin-bottom: 0px !important
}
#den-slot-unlock .den-slot-unlock-item,
#den-slot-unlock-list .den-slot-unlock-item {
    padding: 0 .5em 0 0 !important
}
#den-slot-unlock .den-slot-unlock .common-column-content,
#den-slot-unlock-list .den-slot-unlock .common-column-content {
    padding: 0 .5em 0 .5em !important
}

/* This makes the items visible even if you don't have them all, without compromising clarity on what you're missing. */
.trade-in-icon {
    filter: none !important;
    opacity: 1 !important
}
#den-slot-unlock .den-slot-unlock-item.trade-in-icon-obtained .den-slot-unlock-item-inventory,
#den-slot-unlock-list .den-slot-unlock-item.trade-in-icon-obtained .den-slot-unlock-item-inventory {
    background-color: #00800063 !important;
    color: #1d1d1d
}

/* If you're red-green colourblind, change this colour to whatever works for you. */
#den-slot-unlock .den-slot-unlock-item .den-slot-unlock-item-inventory,
#den-slot-unlock-list .den-slot-unlock-item .den-slot-unlock-item-inventory {
    background-color: #f26c6c7d !important
}

