@-moz-document domain("qzone.qq.com") {
/* 全局变量 */
:root {
	--shadow_color: #3337;
	--norm_whit: #fff;
	--norm_gray:#7a9599;

	--color_1: /*[[color1]]*/;
	--color_2: /*[[color2]]*/;

	--bgcolor_1: #f9f9f9;
	--bgcolor_2: #f0f0f0;

	--angle: /*[[angle1]]*/;
}















/* 屏蔽广告 */
#div_corner_ad_container,
.ck-act,
#QM_Container_100005,
.fn_accessLog_tips.bg2.bor2,
.mod-side-nav.mod-side-nav-recently-used,
.head-detail-info.clearfix,
#tb_vip_li,
[title="QQ空间独立版"],
span.qz-app-flag.wing-fly,
.top-fix-extend,
.hot-msg,
.f-single-biz,
#_qz_zoom_detect,
#idQbossHotbar,
.icon-diamond,
[title="点击查看黄钻特权详情"],
[title="点击查看会员特权详情"],
.sn-count,
span[class="state ui-mr8"],
p[class="other-visitor-info info-item single-item"],
div[class="f-ct  f-ct-passive"] div[class="f-info"] {
	display: none!important;
}
/* 删除红点 */
#tab_menu_mv_cnt,
.icon-refresh-red:after,
.j-videofeed-flashctn {
	display: none;
}
/* 左侧栏 */
li[type="class"],
li[type="app"],
a[title="展开"] {
	display: none!important;
}
[data-version="20171113"] {
	width: 0!important;
	margin-left: 120px;
}




/* 去除边框 */
[target="_blank"] {
	border: none!important;
	border-radius: 0!important;
}
div {
	border: none!important;
}


/* 阴影 */
[data-clicklog="avatar"] > img {
	box-shadow: 0 4px 8px var(--shadow_color);
}

a:hover {
	text-decoration: none;
}


/* 背景颜色 */
li.f-single.f-s-s {
	background: #f000;
	background-image: linear-gradient(var(--angle), var(--color_1), var(--color_2))
}
.background-container {
	min-height: 1000px;
	/* 	position:absolute!important; */
	background-image: linear-gradient(var(--angle), var(--bgcolor_1), var(--bgcolor_2));
	background-size: 100% 100%;
	background-attachment: fixed!important;
}
/* 文字颜色 */
.f-item.f-s-i > div,
span.ui-mr8.state,
.info,
.mention {
	color: #fff!important;
}
.comments-content,
a,
.user-list {
	color: var(--norm_whit)!important;
}
img.q_namecard {
	display: none!important;
	width: 0!important
}
/* 说说加入图片 */
div.qz-poster-bottom {
	background: #e8e8e8;
	margin-top: 15px;
	border-radius: 5px!important;
}


/* 卡片 */
li.f-single.f-s-s {
	padding-top: 50px!important;
	margin-top: 50px;
	box-shadow: 0 4px 8px var(--shadow_color);
	border-radius: 0 12px 0 0;
}
.f-single-head.f-aside {
	margin-top: -67px!important;
	margin-bottom: 20px!important;
}
.info-detail {
	/*时间*/
	float: right;
	margin-top: -21px!important;
}
.f-nick {
	/*昵称*/
	margin-top: 20px;
	width: 80!important;
}
.f-nick > a {
	color: #fff!important;
	font-family: "微软黑雅"!important;
}

[data-cmd="qz_sign"] {
	display: none!important;
}

.user-op {
	/*右上角标*/
	margin-right: -4px!important;
	margin-top: 25px;
}
a.arrow-down > i.fui-icon {
	background-color: #fff;
	border-radius: 12px;
	transition: .3s;
}
a.arrow-down > i.fui-icon:hover {
	transform: rotate(180deg);
}
/*下拉*/
.bubble-i.op-list {
	border-radius: 3px!important;
	box-shadow: 0 4px 8px var(--shadow_color);
}
a #text {
	display: none!important;
}
/*CONTENT*/
.f-single-content.f-wrap {
	padding-top: 10px;
}
/* 手机标识 */
.f-reprint {
	display: none!important;
}
/* 下部 */
.f-op-detail.f-detail {
	padding-top: 20px;
}




[data-cmd="qz_quick_upload_img"] {
	background: #fff0;
}
[placeholder="评论"] {
	border-radius: 5px!important;
	background: #fff4;
}
.comment-box-wrap > div > div > div > div > .qz-inputer.bor2 {
	border-radius: 5px!important;
	background: #fff4;
}
[title="私密评论"] {
	color: var(--norm_whit);
	filter: brightness(300%);
}
/* 子卡片 */
.f-ct {
	background: #F0F0;
}
.f-ct-txtimg.fui-txtimg.fui-txtimg.fui-txtimg-bg.fui-imgbox-row-wrap {
	background: #0000;
}


/* 细节处理 */
#QM_Container_100003,
#QM_Container_333 {
	/* 	height:250px; */
	padding: 9px!important;
}
div[data-hottag="attachbar.pic"] {
	border: none!important;
}
/* 返回顶部 */
#goto_top_btn {
	border-radius: 23px;
	color: var(--norm_gray)!important;
	box-shadow: 0 4px 8px var(--shadow_color);
	transition: .3s;
}
#goto_top_btn:hover {
	box-shadow: 0 2px 8px var(--shadow_color);
}

.qz-poster-ft {
	background: #ff00!important
}
.qz-poster .qz-inputer div.textarea {
	box-sizing: border-box;
	box-shadow: unset!important;
	width: 530px!important;
	height: 136px!important;

	/* 	transition:1s!important; */
	overflow-y: visible!important;
	height: auto!important;
	min-height: 136px;
	padding-right: 0!important;
}
[id="$1_options"] {
	display: none
}
[class="text-place"],
[class="icon-txt"],
[class="bubble-i op-list"] > a {
	color: #7a9599!important;
}
#QM_Mood_Poster_Inner .qz-poster-attach-side {
	margin-top: -10px;
	/* 	margin-left: 48px!important; */
	width: 48px;
}


#QM_Mood_Poster_Inner .item.bor3.bg4.item-other {
	position: absolute!important;
	top: 50px;
	transition: .3s;
}
#QM_Mood_Poster_Inner .item.bor3.bg4.item-other:hover {
	opacity: 0.5;
}
#QM_Mood_Poster_Inner .item.bor3.bg4.item-pic {
	position: absolute!important;
	transition: .3s;
}
#QM_Mood_Poster_Inner .item.bor3.bg4.item-pic:hover {
	opacity: 0.5;
}




#QM_Mood_Poster_Inner [class="qz-inputer bor2"] {
	box-shadow: 0 4px 8px var(--shadow_color);
	padding: 10px;
	border-radius: 3px!important;
}

[class="qz-poster-ft"] {
	margin-top: 20px;
}

.attach > div {
	background: none!important;
}
.layout-nav {
	margin: 0!important;
	background-image: linear-gradient(var(--angle), var(--color_1), var(--color_2));
	height: 80px;
