/*
 * Theme  : Dark Instagram by Mew
 * Version: 1.0.0
 * Author : vednoc
 * Gitlab: https://gitlab.com/vednoc/dark-instagram
 * Discord: https://discord.gg/NpT8PzA
 */
:root {
    --dark: #333333;
    --darker: #222222;
    --light: #C1C1C1;
    --lighter: #E9E9E9;
    --accent: #7289DA;
    --shadow: rgba(0, 0, 0, 0.07);
}

body {
    color: var(--light) !important;
    background-color: var(--dark) !important;
}


/* Remove popups. */
.K2AM_,
.N8xpH {
    display: none;
}


/* Icons. */
[class*="glyphsSprite"],
nav [class*="coreSprite"],
header[class*="PRIVATE"] [class*="logo"],
[class*="coreSpriteClose"] {
    filter: invert(100%) !important;
}


/* Filled icons. */
[class*="Heart__filled"],
[class*="horizontal__filled"] {
    filter: invert(0%) !important;
}



/* Log/sign in. */
.gr27e {
    border-color: var(--darker) !important;
}


/* Input elements. */
.gr27e input,
.gr27e .WZdjL > div {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
}



/* Footer. */
footer {
    background-color: var(--darker) !important;
}



/* Navbar. */
nav > div > div,
header[class*="PRIVATE"] {
    /* box-shadow: 0px 0px 8px 0px var(--shadow); */
    border-color: var(--darker) !important;
    background-color: var(--darker) !important;
}


/* Navbar behind. */
section > nav {
    background-color: var(--dark) !important;
}


/* Broken page nav fix. */
header[class*="PRIVATE"] * {
    color: var(--light) !important;
}



/* Search. */
nav input,
.Di7vw {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
}


/* List. */
nav .drKGC {
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
}


/* Items. */
nav .drKGC a {
    padding: 0px 8px !important;
    border-color: transparent !important;
}

nav .drKGC a:hover,
nav .drKGC .JvDyy {
    background: var(--darker) !important;
}

nav .drKGC a * {
    color: var(--light) !important;
}



/* Main content. */
body,
main {
    background-color: var(--dark) !important;
}


/* Add a profile photo. */
main ._nx5in {
    box-shadow: 0 4px 16px 0 var(--shadow);
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
}



/* Posts. */
main * div[style*="flex-direction: column;"] > article,
main .ltEKP .QBXjJ {
    box-shadow: 0 4px 16px 0 var(--shadow);
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important;
}


/* Usernames. */
a,
a:visited {
    transition: 200ms ease-out !important;
    color: var(--lighter) !important;
}


/* On event. */
a:hover,
span > a.notranslate:hover {
    color: var(--light) !important;
}


/* Accented ones. */
span > a.notranslate {
    color: var(--accent) !important;
}


/* Likes. */
.wwxN2,
.zwZPW {
    background-color: var(--dark) !important;
}


/* Header. */
._914pk {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--darker) !important;
}


/* Comment separator. */
.sH9wk,
.UE9AK {
    border-color: var(--darker) !important;
}


/* Add a comment. */
textarea,
.y9v3U {
    color: var(--lighter) !important;
    border-color: var(--darker) !important;
    background-color: transparent !important;
}



/* Post in lightbox. */
._6oveC {
    /* TODO: add shadow/borders */
    box-shadow: 0 0 0 2px var(--shadow) !important;
    color: var(--light) !important;
    background-color: var(--dark) !important;
}


/* Image background. */
.JyscU ._97aPb {
    background-color: var(--dark) !important;
}


/* Comment separator. */
.JyscU .UE9AK,
.JyscU .Slqrh {
    border-color: var(--darker) !important;
}


/* Video views. */
._9Ytll {
