}


/* On event. */
.pbNvD button:hover {
    color: var(--lighter) !important;
    background-color: var(--darker) !important;
}



/* Suggested/discover section. */
.NP414,
.t0fbY {
    border-color: var(--darker) !important;
    background: var(--dark) !important;
}

@media (min-width: 736px) {
