/*Scrollbars*/
::-webkit-scrollbar {
	width: 8px;
}
::-webkit-scrollbar-track {
	background: transparent;
}
::-webkit-scrollbar-thumb {
	background: rgba(255, 255, 255, .1);
	border-radius: 0px;
}
::-webkit-scrollbar-thumb:hover {
	background: rgba(255, 255, 255, .25);
}
