@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.npostart.nl") {
  /* Episodes overview page */
  .npo-header-franchise {
      min-height: 0 !important;
      padding: 0 !important;
  }

  nav .npo-menu-logo img {
      height: 50px !important;
  }

  /* Navbar minimization */
  div.container {
      max-width: 100%;
  }
  nav {
      padding: 0 !important;
  }

  nav .npo-menu-mijn-npo li a, nav .npo-pages li a {
      padding: 0 !important;
  }

  nav .npo-menu-user {
      padding: 0 .5em !important;
  }

  nav .npo-menu-mijn-npo li a, nav .npo-pages li a,
  nav .npo-menu-user .npo-menu-kids span,
  nav .npo-menu-user a,
  nav .npo-menu-user .btn,
  nav .npo-menu-user .npo-menu-search #npo-search-toggle::after {
      font-size: 0.7em;
  }

  nav .npo-menu-user .btn {
      height: 2em;
  }

  /* Bg image removal */
  .header-fallback-container {
      display: none;
  }

  /* Programme overview */
  .npo-header-franchise-buttons {
      display: none;
  }

  /* Episode */
  .npo-header-episode .npo-header-episode-content {
      padding-top: 70px !important;
  }

  .npo-header-episode .container {
      margin: 0 !important;
  }

  .npo-header-episode .npo-header-episode-content {
      max-width: 100% !important;
  }

  .npo-header-episode h1 {
      display: none;
  }

  .npo-header-episode .npo-header-episode-thumb {
      display: block;
      width: auto;
  }

  .npo-header-episode .npo-header-episode-meta {
      padding: .5em;
      width: 100%;
  }

  /* Format episode info */
  .share-footer,
  .npo-episode-header-program-info,
  #npo-header-episode-read-more {
      display: none !important;
  }

  .npo-header-episode .npo-header-episode-meta .npo-header-episode-meta-content {
      max-height: 999px;
      transition: none !important;
  }

  .npo-header-episode .npo-header-episode-meta .npo-header-episode-meta-content .summary_long {
      display: block !important;
      transition: none !important;
  }
}

@-moz-document domain("resetera.com") {
    /* latin-ext */

    
    
    .MultiQuoteItem .messageGradient {
        position: absolute;
        top: 0px;
        left: 0px;
        right: 0px;
        height: 120px;
        background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
        background: -moz-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
        background: -o-linear-gradient(top, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
        background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 70px, rgb(3, 3, 3) 90%);
    }
    .redactor_toolbar li.redactor_btn_group ul {
        background-color: rgb(56, 56, 56);
        padding: 1px;
        margin: 0;
        overflow: hidden;
        *zoom: 1;
        box-shadow: none;
    }
    .redactor_box {
        font-family: "Fira Sans", sans-serif;
        border: 1px solid rgb(99, 99, 99);
        line-height: 0;
        position: relative;
        overflow: hidden;
    }
    .bbCodeBlock pre,
    .bbCodeBlock .code {
        background-color: rgb(44, 44, 44);
    }
    
    .discussionListItems .unread .title a:hover {
        font-weight: bold;
        color: #bc99d7;
    }
    .Responsive .redactor_toolbar li a {
        text-indent: 0px;
        text-align: center;
        background-image: none !important;
        background: #848484;
        /* color: white; */
    }
    .redactor_toolbar {
        font-size: 0;
