@-moz-document url-prefix("https://tracker.conservice.com/") {
.material-card {
    background-color: #333;
    margin-top: 0 !important;
}
.material-card-body {
    background-color: #333;
    color: #ddd;
    border-color: #111;
    padding: 0;
    box-shadow: none;
}
.material-card-body-unpadded {
    background-color: #333;
    color: #eee;
}
#miscStatsCarousel .material-card-body {
    padding: 5px;
    color: #eee !important;
}
.card-header {
    border: none;
    background-color: transparent;
}
.progBars {
    padding: 10px;
}
.k-alt {
    background-color: rgba(0, 0, 0, 0.2) !important;
}
tr:hover {
    background-color: rgba(255, 255, 255, 0.25) !important;
}
td {
    border: none !important;
}
th, .k-grid-header, .k-grid-header-wrap {
    background-color: rgb(38, 55, 70) !important;
    color: #eee !important;
    border: none !important;
}
input {
    background-color: rgba(255, 255, 255, 0.05) !important;
    color: #eee !important;
    border: 1px solid #222 !important;
}
#WorkLogGrid {
    height: 810px !important;
}
button {
    margin-top: 1px !important;
    margin-bottom: 1px !important;
}
input::placeholder {
    color: #ccc !important;
    opacity: 1;
}
body.site {
    background-color: #222 !important;
    background: repeating-linear-gradient(45deg, rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.1) 2px, rgba(0, 0, 0, 0.25) 2px, rgba(0, 0, 0, 0.25) 4px);
}
.k-picker-wrap {
    background-color: rgba(255, 255, 255, 0.05) !important;
    color: #eee !important;
    border: none !important;
}
.k-select, .k-input, .k-button, .k-state-selected, .k-pager-nav {
    color: #eee !important;
    background-color: #333 !important;
    border: 1px solid #222 !important;
    margin: -1px;
}
.body-content {
    padding-left: 0px;
    margin-left:5px;
}
.sidebar {
    margin-top: 65px;
}
.smoother {
    background-color: rgb(38, 55, 70);
}
.smoother::before {
    background-color: rgba(255, 255, 255, 0.45);
    box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.5);
}
.row .mh-100 {
    padding: 5px;
    margin: 5px;
}
.flex-column.col-3 {
    padding: 0;
    margin: 0;
    padding-top: 10px;
}
.flex-column {
    margin: 0;
    padding-right: 5px;
    padding-left: 5px;
}
#hoursGraph {
    padding-top: 5px;
}
#slides {
}
#overhead {
    margin-top: 22px;
}
text {
    fill: #eee;
}
.input-group-text {
    background-color: #333;
    color: #eee;
    border-color: #222;
}
.k-widget {
    background-color: #333;
    border: none;
    box-shadow: none;
    color: #eee;
}
.k-grid-header {
    border: none !important;
    box-shadow: 0 5px 3px rgb(0, 0, 0, 0.1);
}
.k-grid-content {
    overflow: auto;
}
.k-header, .k-dropdown-wrap {
    border: none;
    box-shadow: none;
}
.k-calendar table, .k-nav-today, .k-calendar-container {
    background-color: #333 !important;
    color: #eee !important;
}
.k-footer {
    border: none !important;
}
.k-nav-today {
    border: none !important;
}
.k-link {
    color: #eee !important;
}
.k-link:hover {
    color: #222 !important;
}
#WorkLogGrid > .k-header > div {
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: baseline;
}
#WorkLogGrid > div.k-header > div > span:nth-child(4) {
    margin: 10px !important;
}
#WorkLogGrid .k-widget {
    flex: 1;
}
}

@-moz-document domain("acfun.cn"), regexp("^https?://(www\\.acfun\\.cn)."), regexp("^https?://(www\\.acfun\\.cn)/+v+.*") {
/* libs

 */
@-webkit-keyframes fadeInDown {
    0% {
        opacity: 0;
        -webkit-transform: translate3d(0, -100%, 0);
        transform: translate3d(0, -100%, 0)
    }
    to {
        opacity: 1;
        -webkit-transform: translateZ(0);
        transform: translateZ(0)
    }
}
@keyframes fadeInDown {
    0% {
        opacity: 0;
        -webkit-transform: translate3d(0, -100%, 0);
        transform: translate3d(0, -100%, 0)
    }
    to {
        opacity: 1;
        -webkit-transform: translateZ(0);
        transform: translateZ(0)
    }
}

@keyframes breathe {
    from {
        box-shadow: inset 0 0 2px rgba(255, 255, 255, 0);
    }
    to {
        box-shadow: inset 0 0 10px rgba(0, 0, 0, .2);
    }
}
@keyframes fade-in {
    0% {
        opacity: 0;
    }
    /*初始状态 透明度为0*/
    40% {
        opacity: 0;
    }
    /*过渡状态 透明度为0*/
    100% {
        opacity: 1;
    }
    /*结束状态 透明度为1*/
}
@-webkit-keyframes fade-in {
    /*针对webkit内核*/
    0% {
        opacity: 0;
    }
    40% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

#wrapper {
    animation: fade-in;
    /*动画名称*/
    animation-duration: 0.5s;
    /*动画持续时间*/
    -webkit-animation: fade-in 1.5s;
    /*针对webkit内核*/
}

/* Test */


/* 主页信息中心 */
#header-guide > li.guide-item.guide-user.user-logined > div {
    animation: fade-in;
    animation-duration: 0.2s;
    -webkit-animation: fade-in 0.3s;
    top: 45px;
}
[data-c-w-header] .header-guide .guide-msg .guide-item-con ul {
    box-shadow: 0px 7px 10px 0px rgba(0, 0, 0, .2);
    animation: fade-in;
    animation-duration: 0.3s;
    -webkit-animation: fade-in .3s;
}
#header-guide > li.guide-item.guide-msg > div {
    opacity: 0.90;
    /*     transform: translateY(-40px); */
    transition-property: opacity;
    transition-duration: 1.5s;
    transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}
#header-guide > li.guide-item.guide-msg > div:hover {
    opacity: 1;
}
.guide-item-con[data-c-w-header] .header-guide .guide-msg .guide-item-con ul {
    box-shadow: 0px 7px 15px 0px rgba(0, 0, 0, .2);
    font-size: 12px;
    background: #fff;
    padding: 0 12px 16px;
    overflow: hidden;
    border-radius: inherit;
}
/* 历史记录 */
[data-c-w-header] .header-guide .guide-item.guide-history .guide-item-con {
    transition: all 300ms;
    width: 350px;
    height: auto;
    box-shadow: 0px 7px 10px 5px rgba(0, 0, 0, .2);
/*     animation: fade-in; */
    animation:fadeInDown;
    animation-duration: 0.3s;
    -webkit-animation: fade-in .3s;
}
/* 历史记录 */
[data-c-w-header] .header-guide .guide-item.guide-history .guide-item-con li a .item-title {
    height: 24px;
    line-height: 26px;
    font-size: 16px;
}

[data-c-w-header] .header-guide .guide-item-con {
    /*     top: 50px; */
    /*     border-radius: 5px 5px 5px 5px; */
    transition: bottom .2s ease, opacity .2s ease;
}

/* 投稿下拉菜单 */
[data-c-w-header] .header-guide .guide-item.guide-upload .guide-item-con {
/*     animation:fadeInDown; */
    width: 100px;
    left: -10px;
    animation: fade-in;
    animation-duration: 0.3s;
    -webkit-animation: fade-in .3s;
}

[data-c-w-header] .header-guide .guide-item-con {
    font-size: 15px;
    left: -30px;
}
/* 隐藏下载手机APP二维码 */
.download-app {
    visibility: hidden;
}

/* 隐藏app引导 */
.app-guide {
    visibility: hidden;
}

/* 信息中心 */
[data-c-w-header] .header-guide .guide-user .user-avatar:hover img {
    transform: scale(1.5);
    transition: transform;
    transition-duration: 0.2s;
    transition-timing-function: cubic-bezier(0.74, 0.01, 0.24, 1);
/*     transition-timing-function: ease-in-out; */
    border-color:pink;
}
#guide-msg-list > li > a:hover {
    color: #212121 !important;
    background-color: #f4f4f4 !important;
}

.header .nav {
    border-bottom: 1px solid #ff0808;
    background-color: #f8f8f8;
}
.header .nav .nav-parent {
    background-color: #f8f8f8;
}
.header .nav .nav-parent ul {
    background-color: #f8f8f8;
}
.header.fixed .nav {
    box-shadow: 0 3px 5px 3px rgba(0, 0, 0, .1);
}
.header .header-top {
    box-shadow: 0 3px 5px 3px rgba(0, 0, 0, .1);
}
[data-c-w-header] .header-guide .guide-msg .guide-item-con ul li {
    padding: 0px;
}
[data-c-w-header] .header-guid {
    padding: 0px;
}
[data-c-w-header] .header-guide .guide-item ul {
    padding: 1px 0px 2px 0px;
}
/* [data-c-w-header] .header-guide .guide-item ul li:hover {
    background-color: #f4f4f4;
} */
#header #nav .guide-msg ul li a {
    font-size: 17px;
    text-align: center;
    padding: 5px 5px;
}

[data-c-w-header] .header-guide .guide-msg .guide-item-con ul li a .badget {
    top: 16px;
    right: 16px;
}
[data-c-w-header] .header-guide .guide-msg .guide-item-con ul {
    padding: 0px;
}
[data-c-w-header] .header-guide .guide-item.guide-history .guide-item-con h3 {
    text-align: center;
}
/* 主站信息中心下啦栏 */
[data-c-w-header] .header-guide .guide-msg .guide-item-con ul li a {
    font-size: 17px;
    text-align: center;
    padding: 10px 0px 10px;
}
/* [data-c-w-header] .header-guide .guide-item ul{
    padding: 1px 0px 20px 5px;
} */
[data-c-w-header] .header-guide .guide-item.guide-history .guide-item-con li a i.device {
    height: 20px;
}

/* Other */
.global-slider {
    box-shadow: 0px 5px 10px #949494;
}

.recommend-video {
    box-shadow: 0px 5px 10px #949494;
}
[data-c-w-header] .search-box .form input:hover {
    border-color: #fd4c5d;
}
#back-top {
    box-shadow: 2px 2px 9px 1px #b4b4b4;
}

/* 主站的navbar */
.nav-fixed {
    box-shadow: 0 3px 5px 3px rgba(0, 0, 0, .1);
}
.top-nav {
    border-bottom: 1px solid #ff0808;
    background-color: #f8f8f8;
    transition: border-bottom 500ms;
}
.header #nav {
    background: #f8f8f8 !important;
}

[data-c-w-header] {
    box-shadow: 0px 2px 10px 1px rgba(0, 0, 0, 0.04);
}
[data-c-w-header] .header-guide .guide-item.guide-history .guide-item-con h3 {
    font-size: 13px;
}
.top-nav .first-container .first-item .first-link:hover{
    background-color: pink;
    transition: background-color 500ms;
    transition-property: background-color;
    transition-duration: 500ms;
    transition-timing-function: cubic-bezier(0.26, 0.85, 0.24, 0.94);
    transition-delay: 0s;
}
.top-nav .first-container .first-item .second-container .second-item .second-link:hover{
    background-color: pink;
    transition: background-color 500ms;
    transition-property: background-color;
    transition-duration: 500ms;
    transition-timing-function: cubic-bezier(0.26, 0.85, 0.24, 0.94);
    transition-delay: 0s;
}
/* .top-nav .first-container .first-item .first-link:hover{
    animation:breath 5s infinite;
    animation-delay:3s;
} */

/* 隐藏浮动描述 */
.float-text {
    visibility: hidden
}

.normal-module .module-left .module-left-header .left-area .header-change {
    border: 0 solid;
}

[data-c-w-header] #nav {
    box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, .1);
}

.article-tab-main .tab-main-header .main-header-item .header-active {
    color: #2ab69d;
    border-color: #fd4c5d;
}

.article-tab-main .tab-main-header .main-header-item > a {
    border-bottom: 1px solid #84d9f8;
}

#danmaku .danmaku-fold {
    border: 0px solid;
    border-radius: 0 0 0 0;
}

.video-description {
    border-bottom: 1px solid #ffffff;
}

.player-box {
    border: 0px solid;
}

.container-controls .control-bar-bottom .input-area {
    border-radius: 0;
}

#main .introduction .tag .add,
#main .introduction .tag .tag-single {
    border: 0px solid;
    border-radius: 0px;
}
#main .introduction .tag .add,
#main .introduction .tag .tag-single:hover {
    border-color: #ff5050;
    background-color: #f4f4f4;
}
[data-c-w-header] .channel-guide:after {
    top: 22px;
}
[data-c-w-header] #nav .nav-sub {
    box-shadow: 0px 10px 5px rgba(0, 0, 0, .1);
}
#main .introduction .up-area .up-interactive .message-to-up {
    border: 0px solid;
}
.edui-container:hover {
    border: 1px solid #ff5454;
}

/* tpm测速 */
#footer .footer-con .footer-avatar-ac {
    right: -40px;
}
}

